import React, { useState, useEffect } from 'react';
import styled, { keyframes } from 'styled-components';
import { motion, AnimatePresence } from 'framer-motion';
import { FiPalette, FiX, FiSave, FiRefreshCw } from 'react-icons/fi';

const slideIn = keyframes`
  from {
    transform: translateX(100%);
    opacity: 0;
  }
  to {
    transform: translateX(0);
    opacity: 1;
  }
`;

const ThemePanel = styled(motion.div)`
  position: fixed;
  top: 0;
  right: 0;
  width: 320px;
  height: 100vh;
  background: ${props => props.theme === 'dark' 
    ? 'linear-gradient(135deg, #1a1a2e 0%, #16213e 50%, #0f3460 100%)'
    : 'linear-gradient(135deg, #f8f9fa 0%, #e9ecef 50%, #dee2e6 100%)'};
  border-left: 2px solid ${props => props.theme === 'dark' ? '#00d4ff' : '#007bff'};
  box-shadow: -5px 0 20px rgba(0, 0, 0, 0.3);
  z-index: 1000;
  overflow-y: auto;
  animation: ${slideIn} 0.3s ease-out;
`;

const ThemeHeader = styled.div`
  padding: 20px;
  border-bottom: 1px solid ${props => props.theme === 'dark' ? '#333' : '#ddd'};
  display: flex;
  justify-content: space-between;
  align-items: center;
`;

const ThemeTitle = styled.h3`
  color: ${props => props.theme === 'dark' ? '#fff' : '#333'};
  margin: 0;
  font-size: 1.2rem;
  display: flex;
  align-items: center;
  gap: 10px;
`;

const CloseButton = styled.button`
  background: none;
  border: none;
  color: ${props => props.theme === 'dark' ? '#fff' : '#333'};
  font-size: 1.5rem;
  cursor: pointer;
  padding: 5px;
  border-radius: 5px;
  transition: all 0.3s ease;

  &:hover {
    background: ${props => props.theme === 'dark' ? '#333' : '#f0f0f0'};
  }
`;

const ThemeContent = styled.div`
  padding: 20px;
`;

const SectionTitle = styled.h4`
  color: ${props => props.theme === 'dark' ? '#fff' : '#333'};
  margin: 0 0 15px 0;
  font-size: 1rem;
`;

const ColorSchemeGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  gap: 10px;
  margin-bottom: 25px;
`;

const ColorScheme = styled(motion.button)`
  padding: 15px;
  border: 2px solid ${props => props.active ? '#00d4ff' : 'transparent'};
  border-radius: 10px;
  background: ${props => props.gradient};
  cursor: pointer;
  transition: all 0.3s ease;
  position: relative;
  overflow: hidden;

  &:hover {
    transform: translateY(-2px);
    box-shadow: 0 5px 15px rgba(0, 0, 0, 0.2);
  }

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: rgba(255, 255, 255, 0.1);
    opacity: 0;
    transition: opacity 0.3s ease;
  }

  &:hover::before {
    opacity: 1;
  }
`;

const SchemeName = styled.span`
  color: white;
  font-weight: 600;
  text-shadow: 1px 1px 2px rgba(0, 0, 0, 0.5);
  font-size: 0.9rem;
`;

const ActionButtons = styled.div`
  display: flex;
  gap: 10px;
  margin-top: 20px;
`;

const ActionButton = styled(motion.button)`
  flex: 1;
  padding: 12px;
  border: none;
  border-radius: 8px;
  font-weight: 600;
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 8px;
  transition: all 0.3s ease;

  &.save {
    background: linear-gradient(135deg, #00d4ff, #0099cc);
    color: white;
  }

  &.reset {
    background: ${props => props.theme === 'dark' ? '#333' : '#f0f0f0'};
    color: ${props => props.theme === 'dark' ? '#fff' : '#333'};
  }

  &:hover {
    transform: translateY(-2px);
    box-shadow: 0 5px 15px rgba(0, 0, 0, 0.2);
  }
`;

const ThemeToggle = styled(motion.button)`
  position: fixed;
  top: 50%;
  right: 20px;
  transform: translateY(-50%);
  width: 50px;
  height: 50px;
  border-radius: 50%;
  border: none;
  background: linear-gradient(135deg, #00d4ff, #0099cc);
  color: white;
  font-size: 1.2rem;
  cursor: pointer;
  box-shadow: 0 5px 15px rgba(0, 212, 255, 0.3);
  z-index: 999;
  transition: all 0.3s ease;

  &:hover {
    transform: translateY(-50%) scale(1.1);
    box-shadow: 0 8px 25px rgba(0, 212, 255, 0.4);
  }
`;

const colorSchemes = [
  {
    id: 'cyberpunk',
    name: 'Cyberpunk',
    gradient: 'linear-gradient(135deg, #ff006e 0%, #8338ec 50%, #3a86ff 100%)'
  },
  {
    id: 'sunset',
    name: 'Sunset',
    gradient: 'linear-gradient(135deg, #ff6b6b 0%, #feca57 50%, #48dbfb 100%)'
  },
  {
    id: 'ocean',
    name: 'Ocean',
    gradient: 'linear-gradient(135deg, #667eea 0%, #764ba2 50%, #f093fb 100%)'
  },
  {
    id: 'forest',
    name: 'Forest',
    gradient: 'linear-gradient(135deg, #11998e 0%, #38ef7d 50%, #56ab2f 100%)'
  },
  {
    id: 'midnight',
    name: 'Midnight',
    gradient: 'linear-gradient(135deg, #2c3e50 0%, #34495e 50%, #3498db 100%)'
  },
  {
    id: 'aurora',
    name: 'Aurora',
    gradient: 'linear-gradient(135deg, #a8edea 0%, #fed6e3 50%, #ffecd2 100%)'
  }
];

const ThemeCustomization = ({ theme, onThemeChange, onClose, isOpen }) => {
  const [selectedScheme, setSelectedScheme] = useState('cyberpunk');

  useEffect(() => {
    // Load saved theme from localStorage
    const savedScheme = localStorage.getItem('themeScheme');
    if (savedScheme) {
      setSelectedScheme(savedScheme);
    }
  }, []);

  const handleSchemeSelect = (schemeId) => {
    setSelectedScheme(schemeId);
  };

  const handleSave = () => {
    localStorage.setItem('themeScheme', selectedScheme);
    onThemeChange({ scheme: selectedScheme });
    onClose(false);
  };

  const handleReset = () => {
    setSelectedScheme('cyberpunk');
    localStorage.removeItem('themeScheme');
    onThemeChange({ scheme: 'cyberpunk' });
  };

  if (!isOpen) {
    return (
      <ThemeToggle
        onClick={() => onClose(true)}
        whileHover={{ scale: 1.1 }}
        whileTap={{ scale: 0.9 }}
      >
        <FiPalette />
      </ThemeToggle>
    );
  }

  return (
    <AnimatePresence>
      <ThemePanel
        theme={theme}
        initial={{ x: 320 }}
        animate={{ x: 0 }}
        exit={{ x: 320 }}
        transition={{ type: 'spring', damping: 25, stiffness: 200 }}
      >
        <ThemeHeader theme={theme}>
          <ThemeTitle theme={theme}>
            <FiPalette />
            Theme Customization
          </ThemeTitle>
          <CloseButton theme={theme} onClick={() => onClose(false)}>
            <FiX />
          </CloseButton>
        </ThemeHeader>

        <ThemeContent>
          <SectionTitle theme={theme}>Color Schemes</SectionTitle>
          <ColorSchemeGrid>
            {colorSchemes.map((scheme) => (
              <ColorScheme
                key={scheme.id}
                gradient={scheme.gradient}
                active={selectedScheme === scheme.id}
                onClick={() => handleSchemeSelect(scheme.id)}
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
              >
                <SchemeName>{scheme.name}</SchemeName>
              </ColorScheme>
            ))}
          </ColorSchemeGrid>

          <ActionButtons>
            <ActionButton
              className="save"
              onClick={handleSave}
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
            >
              <FiSave />
              Save Theme
            </ActionButton>
            <ActionButton
              className="reset"
              theme={theme}
              onClick={handleReset}
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
            >
              <FiRefreshCw />
              Reset
            </ActionButton>
          </ActionButtons>
        </ThemeContent>
      </ThemePanel>
    </AnimatePresence>
  );
};

export default ThemeCustomization;
