import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { useInView } from 'react-intersection-observer';
import { FiMapPin, FiBook, FiTarget, FiUsers, FiUser, FiBookOpen, FiAward } from 'react-icons/fi';

const AboutContainer = styled.section`
  padding: 6rem 2rem;
  max-width: 1200px;
  margin: 0 auto;
`;

const SectionTitle = styled(motion.h2)`
  font-size: clamp(2rem, 5vw, 3rem);
  font-weight: 700;
  text-align: center;
  margin-bottom: 1rem;
  background: linear-gradient(135deg, #6366f1, #8b5cf6);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
`;

const SectionSubtitle = styled(motion.p)`
  text-align: center;
  color: ${props => props.theme === 'dark' ? '#94a3b8' : '#64748b'};
  font-size: 1.2rem;
  margin-bottom: 4rem;
  max-width: 600px;
  margin-left: auto;
  margin-right: auto;
`;

const AboutContent = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 4rem;
  align-items: center;

  @media (max-width: 768px) {
    grid-template-columns: 1fr;
    gap: 2rem;
  }
`;

const AboutText = styled(motion.div)`
  color: ${props => props.theme === 'dark' ? '#cbd5e1' : '#475569'};
  line-height: 1.8;
  font-size: 1.1rem;
`;

const AboutCards = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
  gap: 1.5rem;
`;

const AboutCard = styled(motion.div)`
  background: ${props => props.theme === 'dark' ? 'rgba(255, 255, 255, 0.05)' : 'rgba(0, 0, 0, 0.05)'};
  border: 1px solid ${props => props.theme === 'dark' ? 'rgba(255, 255, 255, 0.1)' : 'rgba(0, 0, 0, 0.1)'};
  border-radius: 1rem;
  padding: 1.5rem;
  backdrop-filter: blur(10px);
  transition: all 0.3s ease;

  &:hover {
    transform: translateY(-5px);
    box-shadow: 0 10px 25px rgba(99, 102, 241, 0.2);
    border-color: #6366f1;
  }
`;

const CardIcon = styled.div`
  color: #6366f1;
  font-size: 2rem;
  margin-bottom: 1rem;
`;

const CardTitle = styled.h3`
  font-size: 1.2rem;
  font-weight: 600;
  margin-bottom: 0.5rem;
  color: ${props => props.theme === 'dark' ? '#ffffff' : '#0a0a0a'};
`;

const CardContent = styled.p`
  color: ${props => props.theme === 'dark' ? '#94a3b8' : '#64748b'};
  font-size: 0.9rem;
  line-height: 1.6;
`;

const StatsContainer = styled(motion.div)`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(150px, 1fr));
  gap: 2rem;
  margin-top: 4rem;
`;

const StatCard = styled(motion.div)`
  text-align: center;
  padding: 2rem 1rem;
  background: ${props => props.theme === 'dark' ? 'rgba(255, 255, 255, 0.05)' : 'rgba(0, 0, 0, 0.05)'};
  border-radius: 1rem;
  border: 1px solid ${props => props.theme === 'dark' ? 'rgba(255, 255, 255, 0.1)' : 'rgba(0, 0, 0, 0.1)'};
`;

const StatNumber = styled.div`
  font-size: 2.5rem;
  font-weight: 700;
  color: #6366f1;
  margin-bottom: 0.5rem;
`;

const StatLabel = styled.div`
  color: ${props => props.theme === 'dark' ? '#94a3b8' : '#64748b'};
  font-size: 0.9rem;
  font-weight: 500;
`;

const About = ({ theme }) => {
  const [ref, inView] = useInView({
    threshold: 0.1,
    triggerOnce: true,
  });

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.2,
        delayChildren: 0.1,
      },
    },
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 30 },
    visible: {
      opacity: 1,
      y: 0,
      transition: {
        duration: 0.6,
        ease: 'easeOut',
      },
    },
  };

  const cardVariants = {
    hidden: { opacity: 0, scale: 0.9 },
    visible: {
      opacity: 1,
      scale: 1,
      transition: {
        duration: 0.5,
        ease: 'easeOut',
      },
    },
  };

  const stats = [
    { number: '4+', label: 'Years Experience' },
    { number: '20+', label: 'Projects Completed' },
    { number: '5+', label: 'Technologies Mastered' },
    { number: '100%', label: 'Client Satisfaction' },
  ];

  return (
    <AboutContainer theme={theme}>
      <AboutContent>
        <AboutHeader
          initial={{ opacity: 0, y: 30 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
        >
          <AboutTitle theme={theme}>About Me</AboutTitle>
          <AboutSubtitle theme={theme}>
            Data Analyst & Full Stack Developer passionate about AI-driven solutions and evidence-based policymaking
          </AboutSubtitle>
        </AboutHeader>

        <AboutGrid>
          <AboutSection
            initial={{ opacity: 0, x: -30 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.6, delay: 0.2 }}
            theme={theme}
          >
            <SectionIcon theme={theme}>
              <FiUser />
            </SectionIcon>
            <SectionTitle theme={theme}>Who I Am</SectionTitle>
            <SectionContent theme={theme}>
              I'm an analytical Data Analyst and Software Developer based in Ontario, Canada, with expertise in socio-economic modeling, 
              forecasting, and large-scale data processing. Currently working at Intact Insurance, I specialize in developing 
              AI-driven automation solutions and scalable backend services. My passion lies in creating actionable insights 
              for evidence-based decision making through advanced analytics and machine learning.
            </SectionContent>
          </AboutSection>

          <AboutSection
            initial={{ opacity: 0, x: 30 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.6, delay: 0.4 }}
            theme={theme}
          >
            <SectionIcon theme={theme}>
              <FiTarget />
            </SectionIcon>
            <SectionTitle theme={theme}>My Mission</SectionTitle>
            <SectionContent theme={theme}>
              To leverage cutting-edge technology and data science to solve complex business challenges. I'm committed to 
              developing scalable solutions that drive efficiency, improve decision-making processes, and create meaningful 
              impact through innovative AI and machine learning applications.
            </SectionContent>
          </AboutSection>

          <AboutSection
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6, delay: 0.6 }}
            theme={theme}
          >
            <SectionIcon theme={theme}>
              <FiBookOpen />
            </SectionIcon>
            <SectionTitle theme={theme}>Education</SectionTitle>
            <SectionContent theme={theme}>
              <strong>Master's in Computer Science with AI Specialization</strong><br/>
              Western University, London (4.0 GPA)<br/>
              <strong>B.Tech in Computer Science (with Honors)</strong><br/>
              A.P.J. Abdul Kalam Technical University
            </SectionContent>
          </AboutSection>

          <AboutSection
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6, delay: 0.8 }}
            theme={theme}
          >
            <SectionIcon theme={theme}>
              <FiAward />
            </SectionIcon>
            <SectionTitle theme={theme}>Certifications & Awards</SectionTitle>
            <SectionContent theme={theme}>
              • AWS Certified Solution Architect (SAA-C03)<br/>
              • Machine Learning Specialization – Stanford University<br/>
              • Employee of the Year Award – TCS (2021)<br/>
              • First Prize – Techwiz (Inter-College AI Competition)
            </SectionContent>
          </AboutSection>
        </AboutGrid>
      </AboutContent>
    </AboutContainer>
  );
};

export default About;
