import React, { createContext, useContext, useState, useEffect } from 'react';
import styled, { keyframes } from 'styled-components';
import { motion, AnimatePresence } from 'framer-motion';
import { 
  FiAward, FiStar, FiTarget, FiTrendingUp, FiHeart, 
  FiEye, FiCode, FiGithub, FiLinkedin, FiMail,
  FiZap, FiCheckCircle, FiX, FiTrophy, FiGift
} from 'react-icons/fi';

// Achievement Context
const AchievementContext = createContext();

export const useAchievements = () => {
  const context = useContext(AchievementContext);
  if (!context) {
    throw new Error('useAchievements must be used within an AchievementProvider');
  }
  return context;
};

// Achievement data
const achievements = [
  {
    id: 'first-visit',
    title: 'First Steps',
    description: 'Welcome to the portfolio!',
    icon: FiStar,
    points: 10,
    category: 'general',
    unlocked: false
  },
  {
    id: 'scroll-explorer',
    title: 'Scroll Explorer',
    description: 'Scrolled through all sections',
    icon: FiEye,
    points: 25,
    category: 'exploration',
    unlocked: false
  },
  {
    id: 'skill-master',
    title: 'Skill Master',
    description: 'Viewed all skill categories',
    icon: FiTarget,
    points: 50,
    category: 'skills',
    unlocked: false
  },
  {
    id: 'project-hunter',
    title: 'Project Hunter',
    description: 'Explored all projects',
    icon: FiCode,
    points: 40,
    category: 'projects',
    unlocked: false
  },
  {
    id: 'social-butterfly',
    title: 'Social Butterfly',
    description: 'Clicked on social links',
    icon: FiHeart,
    points: 30,
    category: 'social',
    unlocked: false
  },
  {
    id: 'easter-egg-hunter',
    title: 'Easter Egg Hunter',
    description: 'Found the secret!',
    icon: FiGift,
    points: 100,
    category: 'secret',
    unlocked: false
  },
  {
    id: 'speed-reader',
    title: 'Speed Reader',
    description: 'Read through content quickly',
    icon: FiTrendingUp,
