// Achievement Provider Component
export const AchievementProvider = ({ children }) => {
  const [userAchievements, setUserAchievements] = useState([]);
  const [notifications, setNotifications] = useState([]);
  const [showTracker, setShowTracker] = useState(false);
  const [showModal, setShowModal] = useState(false);
  const [stats, setStats] = useState({
    totalPoints: 0,
    unlockedCount: 0,
    totalCount: achievements.length
  });

  // Load achievements from localStorage
  useEffect(() => {
    const saved = localStorage.getItem('portfolio-achievements');
    if (saved) {
      setUserAchievements(JSON.parse(saved));
    }
  }, []);

  // Save achievements to localStorage
  useEffect(() => {
    localStorage.setItem('portfolio-achievements', JSON.stringify(userAchievements));

    // Update stats
    const unlockedCount = userAchievements.length;
    const totalPoints = userAchievements.reduce((sum, achievement) => sum + achievement.points, 0);
    setStats({
      totalPoints,
      unlockedCount,
      totalCount: achievements.length
    });
  }, [userAchievements]);

  // Unlock achievement
  const unlockAchievement = (achievementId) => {
    const achievement = achievements.find(a => a.id === achievementId);
    if (!achievement || userAchievements.find(a => a.id === achievementId)) {
      return;
    }

    const unlockedAchievement = { ...achievement, unlocked: true };
    setUserAchievements(prev => [...prev, unlockedAchievement]);

    // Show notification
    const notification = {
      id: Date.now(),
      ...unlockedAchievement
    };
    setNotifications(prev => [...prev, notification]);

    // Remove notification after 5 seconds
    setTimeout(() => {
      setNotifications(prev => prev.filter(n => n.id !== notification.id));
    }, 5000);
  };

  // Check for specific achievements
  const checkScrollAchievement = () => {
    const scrollPosition = window.scrollY;
    const documentHeight = document.documentElement.scrollHeight - window.innerHeight;
    const scrollPercentage = (scrollPosition / documentHeight) * 100;

    if (scrollPercentage > 80 && !userAchievements.find(a => a.id === 'scroll-explorer')) {
      unlockAchievement('scroll-explorer');
    }

    if (scrollPercentage > 50 && !userAchievements.find(a => a.id === 'speed-reader')) {
      unlockAchievement('speed-reader');
    }
  };

  const checkSectionVisits = (sectionId) => {
    const sectionAchievements = {
      'skills': 'skill-master',
      'projects': 'project-hunter'
    };

    const achievementId = sectionAchievements[sectionId];
    if (achievementId && !userAchievements.find(a => a.id === achievementId)) {
      unlockAchievement(achievementId);
    }
  };

  const checkSocialClick = () => {
    if (!userAchievements.find(a => a.id === 'social-butterfly')) {
      unlockAchievement('social-butterfly');
    }
  };

  const checkEasterEgg = () => {
    if (!userAchievements.find(a => a.id === 'easter-egg-hunter')) {
      unlockAchievement('easter-egg-hunter');
    }
  };

  const checkDarkMode = () => {
    if (!userAchievements.find(a => a.id === 'dark-mode-lover')) {
      unlockAchievement('dark-mode-lover');
    }
  };

  const checkRocketClick = () => {
    if (!userAchievements.find(a => a.id === 'rocket-launcher')) {
      unlockAchievement('rocket-launcher');
    }
  };

  const value = {
    userAchievements,
    stats,
    unlockAchievement,
    checkScrollAchievement,
    checkSectionVisits,
    checkSocialClick,
    checkEasterEgg,
    checkDarkMode,
    checkRocketClick,
    showTracker,
    setShowTracker,
    showModal,
    setShowModal
  };

  return (
    <AchievementContext.Provider value={value}>
      {children}

      {/* Achievement Notifications */}
      <AnimatePresence>
        {notifications.map(notification => (
          <AchievementNotification
            key={notification.id}
            initial={{ x: 300, opacity: 0 }}
            animate={{ x: 0, opacity: 1 }}
            exit={{ x: 300, opacity: 0 }}
            transition={{ duration: 0.5 }}
          >
            <AchievementHeader>
              <AchievementIcon>
                <notification.icon />
              </AchievementIcon>
              <div>
                <AchievementTitle>{notification.title}</AchievementTitle>
                <AchievementDescription>{notification.description}</AchievementDescription>
              </div>
              <PointsBadge>+{notification.points}</PointsBadge>
            </AchievementHeader>
          </AchievementNotification>
        ))}
      </AnimatePresence>

      {/* Achievement Tracker */}
      {showTracker && (
        <AchievementTracker
          onClick={() => setShowModal(true)}
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
        >
          <TrackerHeader>
            <FiAward />
            <TrackerTitle>Achievements</TrackerTitle>
          </TrackerHeader>
          <ProgressBar>
            <ProgressFill progress={(stats.unlockedCount / stats.totalCount) * 100} />
          </ProgressBar>
          <StatsText>
            {stats.unlockedCount}/{stats.totalCount} • {stats.totalPoints} pts
          </StatsText>
        </AchievementTracker>
      )}

      {/* Achievements Modal */}
      <AnimatePresence>
        {showModal && (
          <AchievementsModal
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            onClick={() => setShowModal(false)}
          >
            <ModalContent onClick={e => e.stopPropagation()}>
              <ModalHeader>
                <ModalTitle>Achievements</ModalTitle>
                <CloseButton onClick={() => setShowModal(false)}>
                  <FiX />
                </CloseButton>
              </ModalHeader>
              <AchievementGrid>
                {achievements.map(achievement => {
                  const isUnlocked = userAchievements.find(a => a.id === achievement.id);
                  return (
                    <AchievementCard
                      key={achievement.id}
                      unlocked={isUnlocked}
                      whileHover={{ scale: 1.02 }}
                      transition={{ duration: 0.2 }}
                    >
                      <CardIcon>
                        <achievement.icon />
                      </CardIcon>
                      <CardTitle>{achievement.title}</CardTitle>
                      <CardDescription>{achievement.description}</CardDescription>
                      <CardPoints unlocked={isUnlocked}>
                        {isUnlocked ? `+${achievement.points}` : `${achievement.points} pts`}
                      </CardPoints>
                    </AchievementCard>
                  );
                })}
              </AchievementGrid>
            </ModalContent>
          </AchievementsModal>
        )}
      </AnimatePresence>
    </AchievementContext.Provider>
  );
};
