import React, { useState, useEffect } from 'react';
import styled, { keyframes } from 'styled-components';
import { motion, AnimatePresence } from 'framer-motion';
import { 
  FiCode, FiDatabase, FiServer, FiCloud, FiCpu, FiZap,
  FiChevronRight, FiChevronDown, FiStar, FiCheck, FiX,
  FiGitBranch, FiLayers, FiBox, FiGlobe, FiShield
} from 'react-icons/fi';

// Styled Components
const pulse = keyframes`
  0%, 100% { opacity: 1; }
  50% { opacity: 0.5; }
`;

const glow = keyframes`
  0%, 100% { box-shadow: 0 0 5px rgba(99, 102, 241, 0.5); }
  50% { box-shadow: 0 0 20px rgba(99, 102, 241, 0.8); }
`;

const SkillsTreeContainer = styled.div`
  padding: 40px 20px;
  max-width: 1200px;
  margin: 0 auto;
`;

const TreeHeader = styled.div`
  text-align: center;
  margin-bottom: 40px;
`;

const TreeTitle = styled.h2`
  font-size: 2.5rem;
  margin-bottom: 10px;
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
`;

const TreeSubtitle = styled.p`
  font-size: 1.1rem;
  color: ${props => props.theme.textSecondary};
  margin-bottom: 20px;
`;

const TreeStats = styled.div`
  display: flex;
  justify-content: center;
  gap: 30px;
  margin-bottom: 30px;
`;

const StatItem = styled.div`
  text-align: center;
  padding: 15px 25px;
  background: ${props => props.theme.glass};
  border: 1px solid ${props => props.theme.glassBorder};
  border-radius: 15px;
  backdrop-filter: blur(10px);
`;

const StatNumber = styled.div`
  font-size: 2rem;
  font-weight: bold;
  color: ${props => props.theme.primary};
  margin-bottom: 5px;
`;

const StatLabel = styled.div`
  font-size: 0.9rem;
  color: ${props => props.theme.textSecondary};
`;

const TreeVisualization = styled.div`
  position: relative;
  min-height: 600px;
  background: ${props => props.theme.surface};
  border-radius: 20px;
  padding: 40px;
  border: 1px solid ${props => props.theme.border};
  overflow: hidden;
`;

const RootNode = styled(motion.div)`
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  width: 120px;
  height: 120px;
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  color: white;
  font-size: 2rem;
  cursor: pointer;
  box-shadow: 0 10px 30px rgba(102, 126, 234, 0.3);
  animation: ${glow} 2s ease-in-out infinite;
  z-index: 10;

  &:hover {
    transform: translate(-50%, -50%) scale(1.1);
  }
`;

const BranchNode = styled(motion.div)`
  position: absolute;
  width: 100px;
  height: 100px;
  background: ${props => props.unlocked ? 'linear-gradient(135deg, #10b981 0%, #059669 100%)' : 'linear-gradient(135deg, #6b7280 0%, #4b5563 100%)'};
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  color: white;
  font-size: 1.5rem;
  cursor: pointer;
  box-shadow: 0 8px 25px rgba(0, 0, 0, 0.2);
  transition: all 0.3s ease;
  z-index: 5;

  &:hover {
    transform: scale(1.1);
    box-shadow: 0 12px 35px rgba(0, 0, 0, 0.3);
  }

  ${props => props.unlocked && `
    animation: ${pulse} 2s ease-in-out infinite;
  `}
`;

const LeafNode = styled(motion.div)`
  position: absolute;
  width: 80px;
  height: 80px;
  background: ${props => props.unlocked ? 'linear-gradient(135deg, #f59e0b 0%, #d97706 100%)' : 'linear-gradient(135deg, #9ca3af 0%, #6b7280 100%)'};
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  color: white;
  font-size: 1.2rem;
  cursor: pointer;
  box-shadow: 0 6px 20px rgba(0, 0, 0, 0.15);
  transition: all 0.3s ease;
  z-index: 1;

  &:hover {
    transform: scale(1.1);
    box-shadow: 0 10px 30px rgba(0, 0, 0, 0.25);
  }

  ${props => props.unlocked && `
    animation: ${pulse} 2s ease-in-out infinite;
  `}
`;

const ConnectionLine = styled.div`
  position: absolute;
  background: ${props => props.unlocked ? props.theme.primary : props.theme.border};
  height: 2px;
  transform-origin: left center;
  z-index: 0;
  transition: all 0.3s ease;
`;

const NodeLabel = styled.div`
  position: absolute;
  background: ${props => props.theme.surface};
  color: ${props => props.theme.text};
  padding: 5px 10px;
  border-radius: 8px;
  font-size: 0.8rem;
  font-weight: 500;
  white-space: nowrap;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.1);
  border: 1px solid ${props => props.theme.border};
  opacity: 0;
  transition: opacity 0.3s ease;
  pointer-events: none;

  ${props => props.show && `
    opacity: 1;
  `}
`;

const SkillModal = styled(motion.div)`
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: rgba(0, 0, 0, 0.8);
  backdrop-filter: blur(10px);
  z-index: 1000;
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 20px;
`;

const ModalContent = styled.div`
  background: ${props => props.theme.surface};
  border-radius: 20px;
  padding: 30px;
  max-width: 500px;
  width: 100%;
  box-shadow: 0 20px 60px rgba(0, 0, 0, 0.3);
  border: 1px solid ${props => props.theme.border};
`;

const ModalHeader = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 20px;
`;

const ModalTitle = styled.h3`
  margin: 0;
  color: ${props => props.theme.text};
  font-size: 1.5rem;
`;

const CloseButton = styled.button`
  background: none;
  border: none;
  font-size: 1.5rem;
  color: ${props => props.theme.textSecondary};
  cursor: pointer;
  padding: 5px;
  border-radius: 5px;
  transition: all 0.3s ease;

  &:hover {
    color: ${props => props.theme.text};
    background: ${props => props.theme.border};
  }
`;

const SkillIcon = styled.div`
  font-size: 3rem;
  color: ${props => props.theme.primary};
  margin-bottom: 15px;
  text-align: center;
`;

const SkillDescription = styled.p`
  color: ${props => props.theme.textSecondary};
  margin-bottom: 20px;
  line-height: 1.6;
`;

const ProficiencyBar = styled.div`
  margin-bottom: 20px;
`;

const ProficiencyLabel = styled.div`
  display: flex;
  justify-content: space-between;
  margin-bottom: 8px;
  font-size: 0.9rem;
  color: ${props => props.theme.text};
`;

const ProgressBar = styled.div`
  width: 100%;
  height: 8px;
  background: ${props => props.theme.border};
  border-radius: 4px;
  overflow: hidden;
`;

const ProgressFill = styled.div`
  height: 100%;
  background: linear-gradient(90deg, ${props => props.theme.primary}, ${props => props.theme.secondary});
  width: ${props => props.progress}%;
  transition: width 0.3s ease;
`;

const TechnologyTags = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 8px;
  margin-top: 15px;
`;

const Tag = styled.span`
  background: ${props => props.theme.primary};
  color: white;
  padding: 4px 12px;
  border-radius: 12px;
  font-size: 0.8rem;
  font-weight: 500;
`;

// Skill tree data
const skillTreeData = {
  root: {
    id: 'root',
    name: 'Developer',
    icon: FiCode,
    description: 'Core development skills and expertise',
    proficiency: 100,
    technologies: ['Problem Solving', 'System Design', 'Best Practices']
  },
  branches: [
    {
      id: 'frontend',
      name: 'Frontend',
      icon: FiGlobe,
      position: { top: '20%', left: '20%' },
      description: 'Modern frontend development with React, Vue, and modern CSS',
      proficiency: 90,
      technologies: ['React', 'Vue.js', 'TypeScript', 'CSS3', 'HTML5'],
      unlocked: true
    },
    {
      id: 'backend',
      name: 'Backend',
      icon: FiServer,
      position: { top: '20%', right: '20%' },
      description: 'Robust backend development with Node.js, Python, and databases',
      proficiency: 85,
      technologies: ['Node.js', 'Python', 'Express', 'Django', 'PostgreSQL'],
      unlocked: true
    },
    {
      id: 'devops',
      name: 'DevOps',
      icon: FiCloud,
      position: { bottom: '20%', left: '20%' },
      description: 'Infrastructure, deployment, and CI/CD pipelines',
      proficiency: 80,
      technologies: ['Docker', 'AWS', 'GitHub Actions', 'Kubernetes', 'Terraform'],
      unlocked: false
    },
    {
      id: 'ai',
      name: 'AI/ML',
      icon: FiCpu,
      position: { bottom: '20%', right: '20%' },
      description: 'Machine learning, data science, and AI development',
      proficiency: 75,
      technologies: ['TensorFlow', 'PyTorch', 'Scikit-learn', 'Pandas', 'NumPy'],
      unlocked: false
    }
  ],
  leaves: [
    {
      id: 'react',
      name: 'React',
      icon: FiBox,
      parent: 'frontend',
      position: { top: '10%', left: '10%' },
      description: 'Advanced React development with hooks, context, and performance optimization',
      proficiency: 95,
      technologies: ['Hooks', 'Context API', 'Redux', 'Next.js', 'Performance'],
      unlocked: true
    },
    {
      id: 'node',
      name: 'Node.js',
      icon: FiZap,
      parent: 'backend',
      position: { top: '10%', right: '10%' },
      description: 'Server-side JavaScript with Express and RESTful APIs',
      proficiency: 90,
      technologies: ['Express', 'REST APIs', 'Middleware', 'Authentication', 'Testing'],
      unlocked: true
    },
    {
      id: 'docker',
      name: 'Docker',
      icon: FiLayers,
      parent: 'devops',
      position: { bottom: '10%', left: '10%' },
      description: 'Containerization and orchestration with Docker and Kubernetes',
      proficiency: 85,
      technologies: ['Containers', 'Images', 'Compose', 'Kubernetes', 'CI/CD'],
      unlocked: false
    },
    {
      id: 'tensorflow',
      name: 'TensorFlow',
      icon: FiCpu,
      parent: 'ai',
      position: { bottom: '10%', right: '10%' },
      description: 'Deep learning and neural networks with TensorFlow',
      proficiency: 80,
      technologies: ['Neural Networks', 'Deep Learning', 'Computer Vision', 'NLP', 'Model Deployment'],
      unlocked: false
    }
  ]
};

const SkillsTree = () => {
  const [unlockedSkills, setUnlockedSkills] = useState(['root', 'frontend', 'backend', 'react', 'node']);
  const [selectedSkill, setSelectedSkill] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [hoveredNode, setHoveredNode] = useState(null);

  const isUnlocked = (skillId) => unlockedSkills.includes(skillId);

  const unlockSkill = (skillId) => {
    if (!isUnlocked(skillId)) {
      setUnlockedSkills(prev => [...prev, skillId]);
    }
  };

  const handleNodeClick = (skill) => {
    if (isUnlocked(skill.id)) {
      setSelectedSkill(skill);
      setShowModal(true);
    } else {
      unlockSkill(skill.id);
    }
  };

  const calculateConnection = (from, to) => {
    const fromEl = document.getElementById(`node-${from}`);
    const toEl = document.getElementById(`node-${to}`);

    if (!fromEl || !toEl) return {};

    const fromRect = fromEl.getBoundingClientRect();
    const toRect = toEl.getBoundingClientRect();
    const containerRect = document.querySelector('.tree-visualization').getBoundingClientRect();

    const fromX = fromRect.left + fromRect.width / 2 - containerRect.left;
    const fromY = fromRect.top + fromRect.height / 2 - containerRect.top;
    const toX = toRect.left + toRect.width / 2 - containerRect.left;
    const toY = toRect.top + toRect.height / 2 - containerRect.top;

    const length = Math.sqrt(Math.pow(toX - fromX, 2) + Math.pow(toY - fromY, 2));
    const angle = Math.atan2(toY - fromY, toX - fromX) * 180 / Math.PI;

    return {
      width: length,
      left: fromX,
      top: fromY,
      transform: `rotate(${angle}deg)`,
      transformOrigin: '0 50%'
    };
  };

  return (
    <SkillsTreeContainer>
      <TreeHeader>
        <TreeTitle>Skills Tree</TreeTitle>
        <TreeSubtitle>Interactive visualization of my technical expertise</TreeSubtitle>
        <TreeStats>
          <StatItem>
            <StatNumber>{unlockedSkills.length}</StatNumber>
            <StatLabel>Unlocked Skills</StatLabel>
          </StatItem>
          <StatItem>
            <StatNumber>{skillTreeData.branches.length + skillTreeData.leaves.length + 1}</StatNumber>
            <StatLabel>Total Skills</StatLabel>
          </StatItem>
          <StatItem>
            <StatNumber>{Math.round((unlockedSkills.length / (skillTreeData.branches.length + skillTreeData.leaves.length + 1)) * 100)}%</StatNumber>
            <StatLabel>Completion</StatLabel>
          </StatItem>
        </TreeStats>
      </TreeHeader>

      <TreeVisualization className="tree-visualization">
        {/* Root Node */}
        <RootNode
          id="node-root"
          onClick={() => handleNodeClick(skillTreeData.root)}
          onMouseEnter={() => setHoveredNode('root')}
          onMouseLeave={() => setHoveredNode(null)}
          whileHover={{ scale: 1.1 }}
          whileTap={{ scale: 0.95 }}
        >
          <skillTreeData.root.icon />
        </RootNode>

        {/* Branch Nodes */}
        {skillTreeData.branches.map(branch => (
          <React.Fragment key={branch.id}>
            <BranchNode
              id={`node-${branch.id}`}
              unlocked={isUnlocked(branch.id)}
              style={branch.position}
              onClick={() => handleNodeClick(branch)}
              onMouseEnter={() => setHoveredNode(branch.id)}
              onMouseLeave={() => setHoveredNode(null)}
              whileHover={{ scale: 1.1 }}
              whileTap={{ scale: 0.95 }}
            >
              <branch.icon />
            </BranchNode>
            <NodeLabel
              show={hoveredNode === branch.id}
              style={{
                ...branch.position,
                transform: 'translateY(-60px)'
              }}
            >
              {branch.name}
            </NodeLabel>
            <ConnectionLine
              unlocked={isUnlocked(branch.id)}
              style={calculateConnection('root', branch.id)}
            />
          </React.Fragment>
        ))}

        {/* Leaf Nodes */}
        {skillTreeData.leaves.map(leaf => (
          <React.Fragment key={leaf.id}>
            <LeafNode
              id={`node-${leaf.id}`}
              unlocked={isUnlocked(leaf.id)}
              style={leaf.position}
              onClick={() => handleNodeClick(leaf)}
              onMouseEnter={() => setHoveredNode(leaf.id)}
              onMouseLeave={() => setHoveredNode(null)}
              whileHover={{ scale: 1.1 }}
              whileTap={{ scale: 0.95 }}
            >
              <leaf.icon />
            </LeafNode>
            <NodeLabel
              show={hoveredNode === leaf.id}
              style={{
                ...leaf.position,
                transform: 'translateY(-50px)'
              }}
            >
              {leaf.name}
            </NodeLabel>
            <ConnectionLine
              unlocked={isUnlocked(leaf.id)}
              style={calculateConnection(leaf.parent, leaf.id)}
            />
          </React.Fragment>
        ))}
      </TreeVisualization>

      {/* Skill Modal */}
      <AnimatePresence>
        {showModal && selectedSkill && (
          <SkillModal
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            onClick={() => setShowModal(false)}
          >
            <ModalContent onClick={e => e.stopPropagation()}>
              <ModalHeader>
                <ModalTitle>{selectedSkill.name}</ModalTitle>
                <CloseButton onClick={() => setShowModal(false)}>
                  <FiX />
                </CloseButton>
              </ModalHeader>

              <SkillIcon>
                <selectedSkill.icon />
              </SkillIcon>

              <SkillDescription>{selectedSkill.description}</SkillDescription>

              <ProficiencyBar>
                <ProficiencyLabel>
                  <span>Proficiency</span>
                  <span>{selectedSkill.proficiency}%</span>
                </ProficiencyLabel>
                <ProgressBar>
                  <ProgressFill progress={selectedSkill.proficiency} />
                </ProgressBar>
              </ProficiencyBar>

              <TechnologyTags>
                {selectedSkill.technologies.map(tech => (
                  <Tag key={tech}>{tech}</Tag>
                ))}
              </TechnologyTags>
            </ModalContent>
          </SkillModal>
        )}
      </AnimatePresence>
    </SkillsTreeContainer>
  );
};

export default SkillsTree;
