import React, { useState, useEffect } from 'react';
import styled, { keyframes } from 'styled-components';
import { motion, AnimatePresence } from 'framer-motion';
import { 
  FiPalette, FiSettings, FiSave, FiRefreshCw, 
  FiGrid, FiList, FiLayout, FiMonitor, FiSmartphone
} from 'react-icons/fi';

const slideIn = keyframes`
  from {
    transform: translateX(100%);
    opacity: 0;
  }
  to {
    transform: translateX(0);
    opacity: 1;
  }
`;

const pulse = keyframes`
  0%, 100% {
    transform: scale(1);
  }
  50% {
    transform: scale(1.05);
  }
`;

const ThemePanel = styled(motion.div)`
  position: fixed;
  top: 0;
  right: 0;
  width: 350px;
  height: 100vh;
  background: ${props => props.theme === 'dark' 
    ? 'linear-gradient(135deg, #1a1a2e 0%, #16213e 50%, #0f3460 100%)' 
    : 'linear-gradient(135deg, #f8f9fa 0%, #e9ecef 50%, #dee2e6 100%)'};
  border-left: 2px solid ${props => props.theme === 'dark' ? '#00d4ff' : '#007bff'};
  box-shadow: -5px 0 20px rgba(0, 0, 0, 0.3);
  z-index: 1000;
  overflow-y: auto;
  backdrop-filter: blur(10px);
`;

const PanelHeader = styled.div`
  padding: 20px;
  border-bottom: 1px solid ${props => props.theme === 'dark' ? '#333' : '#ddd'};
  display: flex;
  align-items: center;
  justify-content: space-between;
`;

const PanelTitle = styled.h3`
  color: ${props => props.theme === 'dark' ? '#fff' : '#333'};
  margin: 0;
  font-size: 1.2rem;
  font-weight: 600;
`;

const CloseButton = styled.button`
  background: none;
  border: none;
  color: ${props => props.theme === 'dark' ? '#fff' : '#333'};
  font-size: 1.5rem;
  cursor: pointer;
  padding: 5px;
  border-radius: 5px;
  transition: all 0.3s ease;

