const layoutOptions = [
  { id: 'grid', name: 'Grid Layout', icon: FiGrid },
  { id: 'list', name: 'List Layout', icon: FiList },
  { id: 'masonry', name: 'Masonry', icon: FiLayout },
  { id: 'responsive', name: 'Responsive', icon: FiMonitor }
];

const ThemeCustomization = ({ theme, onThemeChange, onLayoutChange, onClose, isOpen }) => {
  const [selectedScheme, setSelectedScheme] = useState('cyberpunk');
  const [selectedLayout, setSelectedLayout] = useState('grid');
  const [primaryColor, setPrimaryColor] = useState('#00d4ff');
  const [secondaryColor, setSecondaryColor] = useState('#ff6b6b');
  const [showNotification, setShowNotification] = useState(false);
  const [notificationMessage, setNotificationMessage] = useState('');

  useEffect(() => {
    // Load saved preferences
    const savedScheme = localStorage.getItem('themeScheme') || 'cyberpunk';
    const savedLayout = localStorage.getItem('layoutType') || 'grid';
    const savedPrimary = localStorage.getItem('primaryColor') || '#00d4ff';
    const savedSecondary = localStorage.getItem('secondaryColor') || '#ff6b6b';

    setSelectedScheme(savedScheme);
    setSelectedLayout(savedLayout);
    setPrimaryColor(savedPrimary);
    setSecondaryColor(savedSecondary);
  }, []);

  const handleSchemeChange = (schemeId) => {
    setSelectedScheme(schemeId);
    showNotificationMessage('Color scheme updated!');
  };

  const handleLayoutChange = (layoutId) => {
    setSelectedLayout(layoutId);
    onLayoutChange(layoutId);
    showNotificationMessage('Layout updated!');
  };

  const handleSave = () => {
    // Save preferences to localStorage
    localStorage.setItem('themeScheme', selectedScheme);
    localStorage.setItem('layoutType', selectedLayout);
    localStorage.setItem('primaryColor', primaryColor);
    localStorage.setItem('secondaryColor', secondaryColor);

    // Apply theme changes
    onThemeChange({
      scheme: selectedScheme,
      primaryColor,
      secondaryColor
    });

    showNotificationMessage('Theme saved successfully!');
  };

  const handleReset = () => {
    setSelectedScheme('cyberpunk');
    setSelectedLayout('grid');
    setPrimaryColor('#00d4ff');
    setSecondaryColor('#ff6b6b');
    showNotificationMessage('Theme reset to default!');
  };

  const showNotificationMessage = (message) => {
    setNotificationMessage(message);
    setShowNotification(true);
    setTimeout(() => setShowNotification(false), 3000);
  };

  return (
    <>
      <ToggleButton 
        theme={theme}
        onClick={() => onClose(!isOpen)}
        title="Theme Customization"
      >
        <FiPalette />
      </ToggleButton>

      <AnimatePresence>
        {isOpen && (
          <ThemePanel
            theme={theme}
            initial={{ x: 350 }}
            animate={{ x: 0 }}
            exit={{ x: 350 }}
            transition={{ type: "spring", damping: 25, stiffness: 200 }}
          >
            <PanelHeader theme={theme}>
              <PanelTitle theme={theme}>
                <FiPalette style={{ marginRight: '8px' }} />
                Theme Customization
              </PanelTitle>
              <CloseButton theme={theme} onClick={() => onClose(false)}>
                Ã—
              </CloseButton>
            </PanelHeader>

            <Section theme={theme}>
              <SectionTitle theme={theme}>
                <FiPalette />
                Color Schemes
              </SectionTitle>
              <ColorSchemeGrid>
                {colorSchemes.map((scheme) => (
                  <ColorScheme
                    key={scheme.id}
                    gradient={scheme.gradient}
                    active={selectedScheme === scheme.id}
                    onClick={() => handleSchemeChange(scheme.id)}
                    title={scheme.name}
                  />
                ))}
              </ColorSchemeGrid>
            </Section>

            <Section theme={theme}>
              <SectionTitle theme={theme}>
                <FiLayout />
                Layout Options
              </SectionTitle>
              <LayoutGrid>
                {layoutOptions.map((layout) => {
                  const IconComponent = layout.icon;
                  return (
                    <LayoutOption
                      key={layout.id}
                      theme={theme}
                      active={selectedLayout === layout.id}
                      onClick={() => handleLayoutChange(layout.id)}
                    >
                      <LayoutIcon theme={theme}>
                        <IconComponent />
                      </LayoutIcon>
                      <LayoutLabel theme={theme}>{layout.name}</LayoutLabel>
                    </LayoutOption>
                  );
                })}
              </LayoutGrid>
            </Section>

            <Section theme={theme}>
              <SectionTitle theme={theme}>
                <FiSettings />
                Custom Colors
              </SectionTitle>
              <ColorPicker>
                <ColorOption>
                  <ColorLabel theme={theme}>Primary Color</ColorLabel>
                  <ColorInput
                    type="color"
                    value={primaryColor}
                    onChange={(e) => setPrimaryColor(e.target.value)}
                  />
                </ColorOption>
                <ColorOption>
                  <ColorLabel theme={theme}>Secondary Color</ColorLabel>
                  <ColorInput
                    type="color"
                    value={secondaryColor}
                    onChange={(e) => setSecondaryColor(e.target.value)}
                  />
                </ColorOption>
              </ColorPicker>
            </Section>

            <Section theme={theme}>
              <ActionButton
                theme={theme}
                variant="primary"
                onClick={handleSave}
              >
                <FiSave />
                Save Theme
              </ActionButton>
              <ActionButton
                theme={theme}
                onClick={handleReset}
              >
                <FiRefreshCw />
                Reset to Default
              </ActionButton>
            </Section>
          </ThemePanel>
        )}
      </AnimatePresence>

      <AnimatePresence>
        {showNotification && (
          <Notification
            theme={theme}
            initial={{ opacity: 0, y: -50 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: -50 }}
            transition={{ duration: 0.3 }}
          >
            {notificationMessage}
          </Notification>
        )}
      </AnimatePresence>
    </>
  );
};

