import React, { createContext, useContext, useState, useEffect } from 'react';
import styled, { keyframes } from 'styled-components';
import { motion, AnimatePresence } from 'framer-motion';
import { FiAward, FiStar, FiEye, FiCode, FiHeart, FiX } from 'react-icons/fi';

// Achievement Context
const AchievementContext = createContext();

export const useAchievements = () => {
  const context = useContext(AchievementContext);
  if (!context) {
    throw new Error('useAchievements must be used within an AchievementProvider');
  }
  return context;
};

// Achievement data
const achievements = [
  {
    id: 'first-visit',
    title: 'First Steps',
    description: 'Welcome to the portfolio!',
    icon: FiStar,
    points: 10,
    unlocked: false
  },
  {
    id: 'scroll-explorer',
    title: 'Scroll Explorer',
    description: 'Scrolled through all sections',
    icon: FiEye,
    points: 25,
    unlocked: false
  },
  {
    id: 'skill-master',
    title: 'Skill Master',
    description: 'Viewed all skill categories',
    icon: FiCode,
    points: 50,
    unlocked: false
  },
  {
    id: 'social-butterfly',
    title: 'Social Butterfly',
    description: 'Clicked on social links',
    icon: FiHeart,
    points: 30,
    unlocked: false
  }
];

// Styled Components
const slideIn = keyframes`
  from {
    transform: translateX(100%);
    opacity: 0;
  }
  to {
    transform: translateX(0);
    opacity: 1;
  }
`;

const AchievementNotification = styled(motion.div)`
  position: fixed;
  top: 20px;
  right: 20px;
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  color: white;
  padding: 20px;
  border-radius: 15px;
  box-shadow: 0 10px 30px rgba(0, 0, 0, 0.3);
  z-index: 10000;
  max-width: 300px;
  backdrop-filter: blur(10px);
  border: 1px solid rgba(255, 255, 255, 0.2);
  animation: ${slideIn} 0.5s ease-out;
`;

const AchievementHeader = styled.div`
  display: flex;
  align-items: center;
  gap: 12px;
  margin-bottom: 8px;
`;

const AchievementIcon = styled.div`
  font-size: 24px;
  color: #ffd700;
`;

const AchievementTitle = styled.h4`
  margin: 0;
  font-size: 16px;
  font-weight: 600;
`;

const AchievementDescription = styled.p`
  margin: 0;
  font-size: 14px;
  opacity: 0.9;
`;

const PointsBadge = styled.span`
  background: rgba(255, 255, 255, 0.2);
  padding: 4px 8px;
  border-radius: 12px;
  font-size: 12px;
  font-weight: 600;
  margin-left: auto;
`;

const AchievementTracker = styled(motion.div)`
  position: fixed;
  bottom: 20px;
  right: 20px;
  background: ${props => props.theme.glass};
  backdrop-filter: blur(20px);
  border: 1px solid ${props => props.theme.glassBorder};
  border-radius: 15px;
  padding: 15px;
  box-shadow: 0 8px 32px ${props => props.theme.shadow};
  z-index: 9999;
  cursor: pointer;
  transition: all 0.3s ease;

  &:hover {
    transform: translateY(-2px);
    box-shadow: 0 12px 40px ${props => props.theme.shadow};
  }
`;

const TrackerHeader = styled.div`
  display: flex;
  align-items: center;
  gap: 8px;
  margin-bottom: 10px;
`;

const TrackerTitle = styled.h4`
  margin: 0;
  font-size: 14px;
  color: ${props => props.theme.text};
`;

const ProgressBar = styled.div`
  width: 100%;
  height: 6px;
  background: ${props => props.theme.border};
  border-radius: 3px;
  overflow: hidden;
  margin-bottom: 8px;
`;

const ProgressFill = styled.div`
  height: 100%;
  background: linear-gradient(90deg, ${props => props.theme.primary}, ${props => props.theme.secondary});
  width: ${props => props.progress}%;
  transition: width 0.3s ease;
`;

const StatsText = styled.p`
  margin: 0;
  font-size: 12px;
  color: ${props => props.theme.textSecondary};
`;

// Achievement Provider Component
export const AchievementProvider = ({ children }) => {
  const [userAchievements, setUserAchievements] = useState([]);
  const [notifications, setNotifications] = useState([]);
  const [showTracker, setShowTracker] = useState(false);
  const [stats, setStats] = useState({
    totalPoints: 0,
    unlockedCount: 0,
    totalCount: achievements.length
  });

  // Load achievements from localStorage
  useEffect(() => {
    const saved = localStorage.getItem('portfolio-achievements');
    if (saved) {
      setUserAchievements(JSON.parse(saved));
    }
  }, []);

  // Save achievements to localStorage
  useEffect(() => {
    localStorage.setItem('portfolio-achievements', JSON.stringify(userAchievements));

    // Update stats
    const unlockedCount = userAchievements.length;
    const totalPoints = userAchievements.reduce((sum, achievement) => sum + achievement.points, 0);
    setStats({
      totalPoints,
      unlockedCount,
      totalCount: achievements.length
    });
  }, [userAchievements]);

  // Unlock achievement
  const unlockAchievement = (achievementId) => {
    const achievement = achievements.find(a => a.id === achievementId);
    if (!achievement || userAchievements.find(a => a.id === achievementId)) {
      return;
    }

    const unlockedAchievement = { ...achievement, unlocked: true };
    setUserAchievements(prev => [...prev, unlockedAchievement]);

    // Show notification
    const notification = {
      id: Date.now(),
      ...unlockedAchievement
    };
    setNotifications(prev => [...prev, notification]);

    // Remove notification after 5 seconds
    setTimeout(() => {
      setNotifications(prev => prev.filter(n => n.id !== notification.id));
    }, 5000);
  };

  // Check for specific achievements
  const checkScrollAchievement = () => {
    const scrollPosition = window.scrollY;
    const documentHeight = document.documentElement.scrollHeight - window.innerHeight;
    const scrollPercentage = (scrollPosition / documentHeight) * 100;

    if (scrollPercentage > 80 && !userAchievements.find(a => a.id === 'scroll-explorer')) {
      unlockAchievement('scroll-explorer');
    }
  };

  const checkSectionVisits = (sectionId) => {
    const sectionAchievements = {
      'skills': 'skill-master'
    };

    const achievementId = sectionAchievements[sectionId];
    if (achievementId && !userAchievements.find(a => a.id === achievementId)) {
      unlockAchievement(achievementId);
    }
  };

  const checkSocialClick = () => {
    if (!userAchievements.find(a => a.id === 'social-butterfly')) {
      unlockAchievement('social-butterfly');
    }
  };

  const value = {
    userAchievements,
    stats,
    unlockAchievement,
    checkScrollAchievement,
    checkSectionVisits,
    checkSocialClick,
    showTracker,
    setShowTracker
  };

  return (
    <AchievementContext.Provider value={value}>
      {children}

      {/* Achievement Notifications */}
      <AnimatePresence>
        {notifications.map(notification => (
          <AchievementNotification
            key={notification.id}
            initial={{ x: 300, opacity: 0 }}
            animate={{ x: 0, opacity: 1 }}
            exit={{ x: 300, opacity: 0 }}
            transition={{ duration: 0.5 }}
          >
            <AchievementHeader>
              <AchievementIcon>
                <notification.icon />
              </AchievementIcon>
              <div>
                <AchievementTitle>{notification.title}</AchievementTitle>
                <AchievementDescription>{notification.description}</AchievementDescription>
              </div>
              <PointsBadge>+{notification.points}</PointsBadge>
            </AchievementHeader>
          </AchievementNotification>
        ))}
      </AnimatePresence>

      {/* Achievement Tracker */}
      {showTracker && (
        <AchievementTracker
          onClick={() => setShowTracker(false)}
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
        >
          <TrackerHeader>
            <FiAward />
            <TrackerTitle>Achievements</TrackerTitle>
          </TrackerHeader>
          <ProgressBar>
            <ProgressFill progress={(stats.unlockedCount / stats.totalCount) * 100} />
          </ProgressBar>
          <StatsText>
            {stats.unlockedCount}/{stats.totalCount} â€¢ {stats.totalPoints} pts
          </StatsText>
        </AchievementTracker>
      )}
    </AchievementContext.Provider>
  );
};
