import React from 'react';
import styled from 'styled-components';
import { FiGlobe, FiCode, FiSmartphone } from 'react-icons/fi';

const ServicesSection = styled.section`
  min-height: 80vh;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  padding: 60px 20px;
  background: ${props => props.theme.surface};
`;

const Title = styled.h1`
  font-size: 2.5rem;
  margin-bottom: 1rem;
  color: ${props => props.theme.primary};
`;

const ServicesList = styled.div`
  display: flex;
  gap: 2rem;
  flex-wrap: wrap;
  justify-content: center;
  margin-top: 2rem;
`;

const ServiceCard = styled.div`
  background: ${props => props.theme.background};
  border: 1px solid ${props => props.theme.border};
  border-radius: 15px;
  padding: 2rem 1.5rem;
  min-width: 220px;
  max-width: 300px;
  box-shadow: 0 8px 32px ${props => props.theme.shadow};
  display: flex;
  flex-direction: column;
  align-items: center;
  text-align: center;
`;

const ServiceIcon = styled.div`
  font-size: 2.5rem;
  color: ${props => props.theme.primary};
  margin-bottom: 1rem;
`;

const ServiceTitle = styled.h2`
  font-size: 1.3rem;
  margin-bottom: 0.5rem;
  color: ${props => props.theme.secondary};
`;

const ServiceDesc = styled.p`
  font-size: 1rem;
  color: ${props => props.theme.textSecondary};
`;

const Services = ({ theme }) => (
  <ServicesSection theme={theme}>
    <Title theme={theme}>Services I Provide</Title>
    <ServicesList>
      <ServiceCard theme={theme}>
        <ServiceIcon theme={theme}><FiCode /></ServiceIcon>
        <ServiceTitle theme={theme}>Website Development</ServiceTitle>
        <ServiceDesc theme={theme}>Modern, responsive websites using the latest technologies tailored to your needs.</ServiceDesc>
      </ServiceCard>
      <ServiceCard theme={theme}>
        <ServiceIcon theme={theme}><FiGlobe /></ServiceIcon>
        <ServiceTitle theme={theme}>Web Applications</ServiceTitle>
        <ServiceDesc theme={theme}>Custom web apps, dashboards, and automation tools for your business or personal projects.</ServiceDesc>
      </ServiceCard>
      <ServiceCard theme={theme}>
        <ServiceIcon theme={theme}><FiSmartphone /></ServiceIcon>
        <ServiceTitle theme={theme}>Mobile-Friendly Design</ServiceTitle>
        <ServiceDesc theme={theme}>Ensuring your site or app looks and works great on all devices.</ServiceDesc>
      </ServiceCard>
    </ServicesList>
  </ServicesSection>
);

export default Services;
