import React, { useState } from 'react';
import styled, { keyframes } from 'styled-components';
import { motion, AnimatePresence } from 'framer-motion';
import { FiCube, FiGlobe, FiType, FiGrid, FiPlay, FiPause } from 'react-icons/fi';
import ThreeDText from './3DText';
import InteractiveGlobe from './InteractiveGlobe';
import ProjectShowcase from './3DProjectShowcase';

const fadeIn = keyframes`
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
`;

const SectionContainer = styled.section`
  min-height: 100vh;
  background: ${props => props.theme === 'dark' 
    ? 'linear-gradient(135deg, #0a0a0a 0%, #1a1a2e 50%, #16213e 100%)'
    : 'linear-gradient(135deg, #f8f9fa 0%, #e9ecef 50%, #dee2e6 100%)'};
  padding: 80px 20px;
  position: relative;
  overflow: hidden;
`;

const SectionHeader = styled.div`
  text-align: center;
  margin-bottom: 60px;
  animation: ${fadeIn} 0.8s ease-out;
`;

const SectionTitle = styled.h2`
  font-size: 3rem;
  font-weight: 700;
  background: linear-gradient(135deg, #00d4ff, #ff6b6b, #4ecdc4);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  margin-bottom: 20px;

  @media (max-width: 768px) {
    font-size: 2rem;
  }
`;

const SectionSubtitle = styled.p`
  font-size: 1.2rem;
  color: ${props => props.theme === 'dark' ? '#94a3b8' : '#64748b'};
  max-width: 600px;
  margin: 0 auto;
  line-height: 1.6;
`;

const NavigationTabs = styled.div`
  display: flex;
  justify-content: center;
  gap: 20px;
  margin-bottom: 40px;
  flex-wrap: wrap;
`;

const TabButton = styled(motion.button)`
  padding: 12px 24px;
  border: 2px solid ${props => props.active 
    ? (props.theme === 'dark' ? '#00d4ff' : '#007bff')
    : (props.theme === 'dark' ? '#374151' : '#e2e8f0')};
  background: ${props => props.active 
    ? (props.theme === 'dark' ? 'rgba(0, 212, 255, 0.1)' : 'rgba(0, 123, 255, 0.1)')
    : 'transparent'};
  color: ${props => props.theme === 'dark' ? '#ffffff' : '#333333'};
  border-radius: 50px;
  cursor: pointer;
  font-weight: 600;
  display: flex;
  align-items: center;
  gap: 8px;
  transition: all 0.3s ease;

  &:hover {
    border-color: ${props => props.theme === 'dark' ? '#00d4ff' : '#007bff'};
    background: ${props => props.theme === 'dark' ? 'rgba(0, 212, 255, 0.1)' : 'rgba(0, 123, 255, 0.1)'};
  }
`;

const ContentContainer = styled.div`
  position: relative;
  min-height: 500px;
`;

const ControlsContainer = styled.div`
  position: absolute;
  top: 20px;
  right: 20px;
  display: flex;
  gap: 10px;
  z-index: 10;
`;

const ControlButton = styled(motion.button)`
  width: 40px;
  height: 40px;
  border-radius: 50%;
  border: none;
  background: ${props => props.theme === 'dark' ? 'rgba(255, 255, 255, 0.1)' : 'rgba(0, 0, 0, 0.1)'};
  color: ${props => props.theme === 'dark' ? '#ffffff' : '#333333'};
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  backdrop-filter: blur(10px);
  transition: all 0.3s ease;

  &:hover {
    background: ${props => props.theme === 'dark' ? 'rgba(255, 255, 255, 0.2)' : 'rgba(0, 0, 0, 0.2)'};
    transform: scale(1.1);
  }
`;

const FeatureDescription = styled(motion.div)`
  position: absolute;
  bottom: 20px;
  left: 20px;
  right: 20px;
  background: ${props => props.theme === 'dark' ? 'rgba(0, 0, 0, 0.8)' : 'rgba(255, 255, 255, 0.9)'};
  color: ${props => props.theme === 'dark' ? '#ffffff' : '#333333'};
  padding: 20px;
  border-radius: 15px;
  backdrop-filter: blur(10px);
  border: 1px solid ${props => props.theme === 'dark' ? 'rgba(255, 255, 255, 0.1)' : 'rgba(0, 0, 0, 0.1)'};
  max-width: 400px;
`;

const FeatureTitle = styled.h3`
  font-size: 1.2rem;
  font-weight: 600;
  margin-bottom: 10px;
  color: ${props => props.theme === 'dark' ? '#00d4ff' : '#007bff'};
`;

const FeatureText = styled.p`
  font-size: 0.9rem;
  line-height: 1.5;
  opacity: 0.9;
`;

const ThreeDSection = ({ theme }) => {
  const [activeTab, setActiveTab] = useState('text');
  const [isPlaying, setIsPlaying] = useState(true);

  const tabs = [
    { id: 'text', label: '3D Text', icon: <FiType /> },
    { id: 'globe', label: 'Interactive Globe', icon: <FiGlobe /> },
    { id: 'showcase', label: 'Project Models', icon: <FiCube /> }
  ];

  const featureDescriptions = {
    text: {
      title: '3D Text Effects',
      description: 'Floating 3D text with particle systems and interactive hover effects. Each letter has depth and responds to user interaction.'
    },
    globe: {
      title: 'Interactive World Map',
      description: 'Rotating 3D globe showing project locations worldwide. Hover over markers to see project details and technology stacks.'
    },
    showcase: {
      title: '3D Project Models',
      description: 'Rotating geometric models representing different project types. Each shape corresponds to a technology category.'
    }
  };

  const renderContent = () => {
    switch (activeTab) {
      case 'text':
        return <ThreeDText theme={theme} />;
      case 'globe':
        return <InteractiveGlobe theme={theme} />;
      case 'showcase':
        return <ProjectShowcase theme={theme} />;
      default:
        return <ThreeDText theme={theme} />;
    }
  };

  return (
    <SectionContainer theme={theme}>
      <SectionHeader>
        <SectionTitle>Interactive 3D Experience</SectionTitle>
        <SectionSubtitle theme={theme}>
          Explore my portfolio through immersive 3D elements powered by Three.js and WebGL
        </SectionSubtitle>
      </SectionHeader>

      <NavigationTabs>
        {tabs.map((tab) => (
          <TabButton
            key={tab.id}
            active={activeTab === tab.id}
            theme={theme}
            onClick={() => setActiveTab(tab.id)}
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
          >
            {tab.icon}
            {tab.label}
          </TabButton>
        ))}
      </NavigationTabs>

      <ContentContainer>
        <ControlsContainer>
          <ControlButton
            theme={theme}
            onClick={() => setIsPlaying(!isPlaying)}
            whileHover={{ scale: 1.1 }}
            whileTap={{ scale: 0.9 }}
          >
            {isPlaying ? <FiPause /> : <FiPlay />}
          </ControlButton>
        </ControlsContainer>

        <AnimatePresence mode="wait">
          <motion.div
            key={activeTab}
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: -20 }}
            transition={{ duration: 0.5 }}
          >
            {renderContent()}
          </motion.div>
        </AnimatePresence>

        <FeatureDescription
          theme={theme}
          initial={{ opacity: 0, x: -20 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ delay: 0.3 }}
        >
          <FeatureTitle>{featureDescriptions[activeTab].title}</FeatureTitle>
          <FeatureText>{featureDescriptions[activeTab].description}</FeatureText>
        </FeatureDescription>
      </ContentContainer>
    </SectionContainer>
  );
};

export default ThreeDSection;
