import React from 'react';
import styled, { keyframes } from 'styled-components';
import { motion } from 'framer-motion';
import { FiTarget, FiUser, FiBookOpen, FiAward, FiCode, FiTrendingUp, FiZap } from 'react-icons/fi';

const gradient = keyframes`
  0% { background-position: 0% 50%; }
  50% { background-position: 100% 50%; }
  100% { background-position: 0% 50%; }
`;

const float = keyframes`
  0%, 100% { transform: translateY(0px); }
  50% { transform: translateY(-10px); }
`;

const pulse = keyframes`
  0%, 100% { transform: scale(1); }
  50% { transform: scale(1.05); }
`;

const AboutContainer = styled.section`
  min-height: 100vh;
  padding: 120px 20px 60px;
  background: ${props => props.theme.background};
  position: relative;
  overflow: hidden;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: 
      radial-gradient(circle at 20% 80%, ${props => props.theme.primary}10 0%, transparent 50%),
      radial-gradient(circle at 80% 20%, ${props => props.theme.secondary}10 0%, transparent 50%),
      radial-gradient(circle at 40% 40%, ${props => props.theme.accent}05 0%, transparent 50%);
    pointer-events: none;
  }
`;

const AboutContent = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  position: relative;
  z-index: 2;
`;

const AboutHeader = styled(motion.div)`
  text-align: center;
  margin-bottom: 6rem;
`;

const AboutTitle = styled.h1`
  font-size: clamp(3rem, 6vw, 5rem);
  font-weight: 800;
  background: linear-gradient(-45deg, ${props => props.theme.primary}, ${props => props.theme.secondary}, ${props => props.theme.accent});
  background-size: 400% 400%;
  animation: ${gradient} 3s ease infinite;
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  margin-bottom: 1.5rem;
  letter-spacing: -0.02em;
`;

const AboutSubtitle = styled.p`
  font-size: 1.3rem;
  color: ${props => props.theme.textSecondary};
  max-width: 700px;
  margin: 0 auto;
  line-height: 1.7;
  font-weight: 400;
`;

const MainSection = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 4rem;
  align-items: center;
  margin-bottom: 6rem;

  @media (max-width: 768px) {
    grid-template-columns: 1fr;
    gap: 3rem;
  }
`;

const LeftContent = styled(motion.div)`
  position: relative;
`;

const ProfileImage = styled(motion.div)`
  width: 400px;
  height: 400px;
  border-radius: 50%;
  background: linear-gradient(135deg, ${props => props.theme.primary}, ${props => props.theme.secondary}, ${props => props.theme.accent});
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 8rem;
  color: white;
  margin: 0 auto;
  position: relative;
  animation: ${float} 6s ease-in-out infinite;

  &::before {
    content: '';
    position: absolute;
    inset: -10px;
    border-radius: 50%;
    background: linear-gradient(135deg, ${props => props.theme.primary}, ${props => props.theme.secondary}, ${props => props.theme.accent});
    opacity: 0.3;
    filter: blur(20px);
    animation: ${pulse} 4s ease-in-out infinite;
  }

  @media (max-width: 768px) {
    width: 300px;
    height: 300px;
    font-size: 6rem;
  }
`;

const RightContent = styled(motion.div)`
  display: flex;
  flex-direction: column;
  gap: 2rem;
`;

const IntroText = styled.div`
  font-size: 1.4rem;
