import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { useInView } from 'react-intersection-observer';
import { FiCalendar, FiMapPin } from 'react-icons/fi';

const ExperienceContainer = styled.section`
  padding: 6rem 2rem;
  max-width: 1200px;
  margin: 0 auto;
`;

const SectionTitle = styled(motion.h2)`
  font-size: clamp(2rem, 5vw, 3rem);
  font-weight: 700;
  text-align: center;
  margin-bottom: 1rem;
  background: linear-gradient(135deg, #6366f1, #8b5cf6);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
`;

const SectionSubtitle = styled(motion.p)`
  text-align: center;
  color: ${props => props.theme === 'dark' ? '#94a3b8' : '#64748b'};
  font-size: 1.2rem;
  margin-bottom: 4rem;
  max-width: 600px;
  margin-left: auto;
  margin-right: auto;
`;

const TimelineContainer = styled.div`
  position: relative;
  max-width: 800px;
  margin: 0 auto;

  &::before {
    content: '';
    position: absolute;
    left: 50%;
    top: 0;
    bottom: 0;
    width: 2px;
    background: linear-gradient(to bottom, #6366f1, #8b5cf6);
    transform: translateX(-50%);

    @media (max-width: 768px) {
      left: 2rem;
    }
  }
`;

const TimelineItem = styled(motion.div)`
  position: relative;
  margin-bottom: 3rem;
  display: flex;
  align-items: center;

  &:nth-child(odd) {
    flex-direction: row;

    @media (max-width: 768px) {
      flex-direction: column;
      align-items: flex-start;
      margin-left: 4rem;
    }
  }

  &:nth-child(even) {
    flex-direction: row-reverse;

    @media (max-width: 768px) {
      flex-direction: column;
      align-items: flex-start;
      margin-left: 4rem;
    }
  }
`;

const TimelineContent = styled.div`
  width: 45%;
  background: ${props => props.theme === 'dark' ? 'rgba(255, 255, 255, 0.05)' : 'rgba(0, 0, 0, 0.05)'};
  border: 1px solid ${props => props.theme === 'dark' ? 'rgba(255, 255, 255, 0.1)' : 'rgba(0, 0, 0, 0.1)'};
  border-radius: 1rem;
  padding: 2rem;
  backdrop-filter: blur(10px);
  transition: all 0.3s ease;

  &:hover {
    transform: translateY(-5px);
    box-shadow: 0 10px 25px rgba(99, 102, 241, 0.2);
    border-color: #6366f1;
  }

  @media (max-width: 768px) {
    width: 100%;
  }
`;

const TimelineDot = styled.div`
  position: absolute;
  left: 50%;
  top: 50%;
  width: 20px;
  height: 20px;
  background: #6366f1;
  border: 4px solid ${props => props.theme === 'dark' ? '#0a0a0a' : '#ffffff'};
  border-radius: 50%;
  transform: translate(-50%, -50%);
  z-index: 10;

  @media (max-width: 768px) {
    left: 2rem;
  }
`;

const JobTitle = styled.h3`
  font-size: 1.3rem;
  font-weight: 600;
  margin-bottom: 0.5rem;
  color: ${props => props.theme === 'dark' ? '#ffffff' : '#0a0a0a'};
`;

const CompanyName = styled.h4`
  font-size: 1.1rem;
  font-weight: 500;
  color: #6366f1;
  margin-bottom: 0.5rem;
`;

const JobMeta = styled.div`
  display: flex;
  align-items: center;
  gap: 1rem;
  margin-bottom: 1rem;
  flex-wrap: wrap;
`;

const MetaItem = styled.div`
  display: flex;
  align-items: center;
  gap: 0.5rem;
  color: ${props => props.theme === 'dark' ? '#94a3b8' : '#64748b'};
  font-size: 0.9rem;
`;

const JobDescription = styled.p`
  color: ${props => props.theme === 'dark' ? '#cbd5e1' : '#475569'};
  line-height: 1.6;
  margin-bottom: 1rem;
`;

const JobAchievements = styled.ul`
  color: ${props => props.theme === 'dark' ? '#cbd5e1' : '#475569'};
  line-height: 1.6;
  padding-left: 1.5rem;
`;

const JobAchievement = styled.li`
  margin-bottom: 0.5rem;
`;

const TechStack = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 0.5rem;
  margin-top: 1rem;
`;

const TechTag = styled.span`
  background: ${props => props.theme === 'dark' ? 'rgba(99, 102, 241, 0.2)' : 'rgba(99, 102, 241, 0.1)'};
  color: #6366f1;
  padding: 0.25rem 0.75rem;
  border-radius: 50px;
  font-size: 0.8rem;
  font-weight: 500;
  border: 1px solid rgba(99, 102, 241, 0.3);
`;

const Experience = ({ theme }) => {
  const [ref, inView] = useInView({
    threshold: 0.1,
    triggerOnce: true,
  });

  const experiences = [
    {
      id: 1,
      title: 'AI Engineer',
      company: 'TechCorp Solutions',
      location: 'Toronto, ON',
      period: 'Jan 2023 - Present',
      description: 'Leading AI/ML initiatives and developing scalable machine learning solutions for enterprise clients.',
      achievements: [
        'Developed and deployed BERT-based offensive speech detection model achieving 94% accuracy',
        'Built real-time data processing pipeline using Apache Kafka and Spark',
        'Implemented CI/CD pipelines for ML model deployment using GitHub Actions and Docker',
        'Reduced model inference time by 60% through optimization and caching strategies'
      ],
      tech: ['Python', 'TensorFlow', 'BERT', 'Apache Kafka', 'Docker', 'AWS', 'GitHub Actions']
    },
    {
      id: 2,
      title: 'Cloud Developer',
      company: 'CloudTech Inc.',
      location: 'Remote',
      period: 'Jun 2022 - Dec 2022',
      description: 'Specialized in cloud infrastructure and DevOps practices for scalable applications.',
      achievements: [
        'Designed and implemented cloud-native architecture using AWS services',
        'Automated infrastructure provisioning with Terraform and Ansible',
        'Set up monitoring and alerting systems using CloudWatch and SNS',
        'Improved deployment efficiency by 80% through CI/CD automation'
      ],
      tech: ['AWS', 'Terraform', 'Docker', 'Kubernetes', 'Python', 'Ansible', 'CloudWatch']
    },
    {
      id: 3,
      title: 'Software Engineer',
      company: 'InnovateSoft',
      location: 'Vancouver, BC',
      period: 'Jan 2022 - May 2022',
      description: 'Full-stack development with focus on modern web technologies and user experience.',
      achievements: [
        'Built responsive web applications using React and Node.js',
        'Implemented RESTful APIs with FastAPI and PostgreSQL',
        'Integrated third-party services and payment gateways',
        'Collaborated with design team to improve UI/UX and accessibility'
      ],
      tech: ['React', 'Node.js', 'FastAPI', 'PostgreSQL', 'TypeScript', 'Docker']
    },
    {
      id: 4,
      title: 'ML Research Intern',
      company: 'AI Research Lab',
      location: 'University of Toronto',
      period: 'May 2021 - Dec 2021',
      description: 'Research and development in computer vision and natural language processing.',
      achievements: [
        'Researched state-of-the-art computer vision models for object detection',
        'Published research paper on emotion recognition using deep learning',
        'Developed prototype applications using PyTorch and OpenCV',
        'Presented findings at university research symposium'
      ],
      tech: ['Python', 'PyTorch', 'OpenCV', 'Computer Vision', 'NLP', 'Research']
    }
  ];

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.3,
        delayChildren: 0.1,
      },
    },
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 50 },
    visible: {
      opacity: 1,
      y: 0,
      transition: {
        duration: 0.6,
        ease: 'easeOut',
      },
    },
  };

  return (
    <ExperienceContainer ref={ref}>
      <motion.div
        variants={containerVariants}
        initial="hidden"
        animate={inView ? 'visible' : 'hidden'}
      >
        <SectionTitle variants={itemVariants}>
          Work Experience
        </SectionTitle>

        <SectionSubtitle variants={itemVariants} theme={theme}>
          My professional journey and career progression
        </SectionSubtitle>

        <TimelineContainer>
          {experiences.map((experience, index) => (
            <TimelineItem key={experience.id} variants={itemVariants}>
              <TimelineDot theme={theme} />
              <TimelineContent theme={theme}>
                <JobTitle theme={theme}>{experience.title}</JobTitle>
                <CompanyName>{experience.company}</CompanyName>

                <JobMeta>
                  <MetaItem theme={theme}>
                    <FiCalendar />
                    {experience.period}
                  </MetaItem>
                  <MetaItem theme={theme}>
                    <FiMapPin />
                    {experience.location}
                  </MetaItem>
                </JobMeta>

                <JobDescription theme={theme}>
                  {experience.description}
                </JobDescription>

                <JobAchievements>
                  {experience.achievements.map((achievement, achievementIndex) => (
                    <JobAchievement key={achievementIndex} theme={theme}>
                      {achievement}
                    </JobAchievement>
                  ))}
                </JobAchievements>

                <TechStack>
                  {experience.tech.map((tech, techIndex) => (
                    <TechTag key={techIndex} theme={theme}>
                      {tech}
                    </TechTag>
                  ))}
                </TechStack>
              </TimelineContent>
            </TimelineItem>
          ))}
        </TimelineContainer>
      </motion.div>
    </ExperienceContainer>
  );
};

export default Experience;
