import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { useInView } from 'react-intersection-observer';
import { FiMapPin, FiBook, FiTarget, FiUsers } from 'react-icons/fi';

const AboutContainer = styled.section`
  padding: 6rem 2rem;
  max-width: 1200px;
  margin: 0 auto;
`;

const SectionTitle = styled(motion.h2)`
  font-size: clamp(2rem, 5vw, 3rem);
  font-weight: 700;
  text-align: center;
  margin-bottom: 1rem;
  background: linear-gradient(135deg, #6366f1, #8b5cf6);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
`;

const SectionSubtitle = styled(motion.p)`
  text-align: center;
  color: ${props => props.theme === 'dark' ? '#94a3b8' : '#64748b'};
  font-size: 1.2rem;
  margin-bottom: 4rem;
  max-width: 600px;
  margin-left: auto;
  margin-right: auto;
`;

const AboutContent = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 4rem;
  align-items: center;

  @media (max-width: 768px) {
    grid-template-columns: 1fr;
    gap: 2rem;
  }
`;

const AboutText = styled(motion.div)`
  color: ${props => props.theme === 'dark' ? '#cbd5e1' : '#475569'};
  line-height: 1.8;
  font-size: 1.1rem;
`;

const AboutCards = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
  gap: 1.5rem;
`;

const AboutCard = styled(motion.div)`
  background: ${props => props.theme === 'dark' ? 'rgba(255, 255, 255, 0.05)' : 'rgba(0, 0, 0, 0.05)'};
  border: 1px solid ${props => props.theme === 'dark' ? 'rgba(255, 255, 255, 0.1)' : 'rgba(0, 0, 0, 0.1)'};
  border-radius: 1rem;
  padding: 1.5rem;
  backdrop-filter: blur(10px);
  transition: all 0.3s ease;

  &:hover {
    transform: translateY(-5px);
    box-shadow: 0 10px 25px rgba(99, 102, 241, 0.2);
    border-color: #6366f1;
  }
`;

const CardIcon = styled.div`
  color: #6366f1;
  font-size: 2rem;
  margin-bottom: 1rem;
`;

const CardTitle = styled.h3`
  font-size: 1.2rem;
  font-weight: 600;
  margin-bottom: 0.5rem;
  color: ${props => props.theme === 'dark' ? '#ffffff' : '#0a0a0a'};
`;

const CardContent = styled.p`
  color: ${props => props.theme === 'dark' ? '#94a3b8' : '#64748b'};
  font-size: 0.9rem;
  line-height: 1.6;
`;

const StatsContainer = styled(motion.div)`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(150px, 1fr));
  gap: 2rem;
  margin-top: 4rem;
`;

const StatCard = styled(motion.div)`
  text-align: center;
  padding: 2rem 1rem;
  background: ${props => props.theme === 'dark' ? 'rgba(255, 255, 255, 0.05)' : 'rgba(0, 0, 0, 0.05)'};
  border-radius: 1rem;
  border: 1px solid ${props => props.theme === 'dark' ? 'rgba(255, 255, 255, 0.1)' : 'rgba(0, 0, 0, 0.1)'};
`;

const StatNumber = styled.div`
  font-size: 2.5rem;
  font-weight: 700;
  color: #6366f1;
  margin-bottom: 0.5rem;
`;

const StatLabel = styled.div`
  color: ${props => props.theme === 'dark' ? '#94a3b8' : '#64748b'};
  font-size: 0.9rem;
  font-weight: 500;
`;

const About = ({ theme }) => {
  const [ref, inView] = useInView({
    threshold: 0.1,
    triggerOnce: true,
  });

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.2,
        delayChildren: 0.1,
      },
    },
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 30 },
    visible: {
      opacity: 1,
      y: 0,
      transition: {
        duration: 0.6,
        ease: 'easeOut',
      },
    },
  };

  const cardVariants = {
    hidden: { opacity: 0, scale: 0.9 },
    visible: {
      opacity: 1,
      scale: 1,
      transition: {
        duration: 0.5,
        ease: 'easeOut',
      },
    },
  };

  const stats = [
    { number: '4+', label: 'Years Experience' },
    { number: '20+', label: 'Projects Completed' },
    { number: '5+', label: 'Technologies Mastered' },
    { number: '100%', label: 'Client Satisfaction' },
  ];

  return (
    <AboutContainer ref={ref}>
      <motion.div
        variants={containerVariants}
        initial="hidden"
        animate={inView ? 'visible' : 'hidden'}
      >
        <SectionTitle variants={itemVariants}>
          About Me
        </SectionTitle>

        <SectionSubtitle variants={itemVariants} theme={theme}>
          Get to know me better and understand what drives my passion for technology
        </SectionSubtitle>

        <AboutContent>
          <AboutText variants={itemVariants} theme={theme}>
            <p>
              I'm a software developer and AI engineer based in Ontario, Canada, with a deep passion 
              for solving real-world problems through innovative technology solutions. My journey in 
              tech began with a curiosity about how machines can learn and adapt, which has evolved 
              into a comprehensive expertise spanning machine learning, cloud computing, and software engineering.
            </p>
            <br />
            <p>
              I specialize in building scalable AI systems, deploying machine learning models in production, 
              and creating cloud-native applications that can handle real-world demands. My work focuses on 
              the intersection of artificial intelligence and practical applications, whether that's detecting 
              offensive speech in real-time, building intelligent chatbots, or creating computer vision systems.
            </p>
            <br />
            <p>
              When I'm not coding, you'll find me contributing to open-source projects, exploring new 
              technologies, or sharing knowledge with the developer community. I believe in continuous 
              learning and staying at the forefront of technological innovation.
            </p>
          </AboutText>

          <AboutCards>
            <AboutCard
              variants={cardVariants}
              theme={theme}
              whileHover={{ scale: 1.05 }}
            >
              <CardIcon>
                <FiMapPin />
              </CardIcon>
              <CardTitle theme={theme}>Location</CardTitle>
              <CardContent theme={theme}>
                Ontario, Canada
              </CardContent>
            </AboutCard>

            <AboutCard
              variants={cardVariants}
              theme={theme}
              whileHover={{ scale: 1.05 }}
            >
              <CardIcon>
                <FiBook />
              </CardIcon>
              <CardTitle theme={theme}>Education</CardTitle>
              <CardContent theme={theme}>
                Computer Science & Engineering
              </CardContent>
            </AboutCard>

            <AboutCard
              variants={cardVariants}
              theme={theme}
              whileHover={{ scale: 1.05 }}
            >
              <CardIcon>
                <FiTarget />
              </CardIcon>
              <CardTitle theme={theme}>Focus</CardTitle>
              <CardContent theme={theme}>
                AI/ML, Cloud Computing, Full-Stack Development
              </CardContent>
            </AboutCard>

            <AboutCard
              variants={cardVariants}
              theme={theme}
              whileHover={{ scale: 1.05 }}
            >
              <CardIcon>
                <FiUsers />
              </CardIcon>
              <CardTitle theme={theme}>Collaboration</CardTitle>
              <CardContent theme={theme}>
                Open Source Contributor & Team Player
              </CardContent>
            </AboutCard>
          </AboutCards>
        </AboutContent>

        <StatsContainer variants={itemVariants}>
          {stats.map((stat, index) => (
            <StatCard
              key={index}
              theme={theme}
              whileHover={{ scale: 1.05 }}
              transition={{ duration: 0.2 }}
            >
              <StatNumber>{stat.number}</StatNumber>
              <StatLabel theme={theme}>{stat.label}</StatLabel>
            </StatCard>
          ))}
        </StatsContainer>
      </motion.div>
    </AboutContainer>
  );
};

export default About;
