import React, { useCallback } from 'react';
import { initParticlesEngine } from '@tsparticles/react';
import Particles from '@tsparticles/react';

const ParticlesBackground = () => {
  const particlesInit = useCallback(async (engine) => {
    await initParticlesEngine(engine);
  }, []);

  const particlesLoaded = useCallback(async (container) => {
    // console.log(container);
  }, []);

  return (
    <div style={{
      position: 'fixed',
      top: 0,
      left: 0,
      width: '100%',
      height: '100%',
      zIndex: -1,
      background: 'linear-gradient(135deg, #0a0a0a 0%, #1a1a2e 50%, #16213e 100%)',
    }}>
      <Particles
        id="tsparticles"
        init={particlesInit}
        loaded={particlesLoaded}
        options={{
          fullScreen: {
            enable: false,
            zIndex: -1
          },
          background: {
            color: {
              value: "transparent",
            },
          },
          fpsLimit: 60,
          particles: {
            color: {
              value: ["#00fff0", "#6366f1", "#ec4899", "#f59e0b"],
            },
            links: {
              color: "#00fff0",
              distance: 150,
              enable: true,
              opacity: 0.3,
              width: 1,
              triangles: {
                enable: true,
                opacity: 0.1
              }
            },
            collisions: {
              enable: true,
            },
            move: {
              direction: "none",
              enable: true,
              outModes: {
                default: "bounce",
              },
              random: false,
              speed: 1,
              straight: false,
              trail: {
                enable: true,
                length: 5,
                fill: { color: "#000000" }
              }
            },
            number: {
              density: {
                enable: true,
                area: 800,
              },
              value: 80,
            },
            opacity: {
              value: 0.8,
              animation: {
                enable: true,
                speed: 1,
                minimumValue: 0.1,
              }
            },
            shape: {
              type: "circle",
            },
            size: {
              value: { min: 1, max: 3 },
              animation: {
                enable: true,
                speed: 2,
                minimumValue: 0.1,
              }
            },
            twinkle: {
              particles: {
                enable: true,
                color: "#00fff0",
                frequency: 0.05,
                opacity: 1
              }
            },
            life: {
              duration: {
                sync: false,
                value: 20
              },
              count: 1
            },
            rotate: {
              value: {
                min: 0,
                max: 360
              },
              direction: "random",
              animation: {
                enable: true,
                speed: 5
              },
              move: true
            },
            orbit: {
              animation: {
                count: 0,
                enable: false,
                speed: 1,
                sync: false
              },
              enable: false,
              radius: 0,
              rotation: {
                random: {
                  enable: false,
                  minimumValue: 0
                },
                value: 45
              }
            }
          },
          detectRetina: true,
          interactivity: {
            events: {
              onClick: {
                enable: true,
                mode: "push",
              },
              onHover: {
                enable: true,
                mode: "repulse",
              },
              resize: true,
            },
            modes: {
              push: {
                quantity: 4,
              },
              repulse: {
                distance: 100,
                duration: 0.4,
              },
            },
          },
          emitters: {
            direction: "none",
            life: {
              count: 0,
              duration: 0.1,
              delay: 0.1
            },
              rate: {
                quantity: 1,
                delay: 0.15
              },
              size: {
                width: 0,
                height: 0
              },
              spawnColor: {
                value: "#00fff0"
              }
          }
        }}
      />
    </div>
  );
};

export default ParticlesBackground;
