import React, { useState, useEffect, useRef } from 'react';
import styled, { keyframes } from 'styled-components';
import { motion, AnimatePresence } from 'framer-motion';
import { 
  FiPlay, FiPause, FiSkipBack, FiSkipForward, FiVolume2, 
  FiVolumeX, FiShuffle, FiRepeat, FiList, FiHeart,
  FiMusic, FiHeadphones, FiRadio, FiDisc
} from 'react-icons/fi';

const fadeIn = keyframes`
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
`;

const pulse = keyframes`
  0%, 100% {
    transform: scale(1);
  }
  50% {
    transform: scale(1.05);
  }
`;

const rotate = keyframes`
  from {
    transform: rotate(0deg);
  }
  to {
    transform: rotate(360deg);
  }
`;

const wave = keyframes`
  0%, 100% {
    height: 5px;
  }
  50% {
    height: 20px;
  }
`;

const PlayerContainer = styled(motion.div)`
  position: fixed;
  bottom: 20px;
  left: 50%;
  transform: translateX(-50%);
  background: ${props => props.theme === 'dark' 
    ? 'rgba(0, 0, 0, 0.9)' 
    : 'rgba(255, 255, 255, 0.9)'};
  border: 1px solid ${props => props.theme === 'dark' 
    ? 'rgba(255, 255, 255, 0.1)' 
    : 'rgba(0, 0, 0, 0.1)'};
  border-radius: 25px;
  padding: 15px 25px;
  backdrop-filter: blur(20px);
  box-shadow: 0 10px 30px rgba(0, 0, 0, 0.3);
  z-index: 1000;
  display: flex;
  align-items: center;
  gap: 20px;
  min-width: 400px;
  max-width: 600px;

  @media (max-width: 768px) {
    min-width: 300px;
    padding: 10px 15px;
    gap: 15px;
  }
`;

const AlbumArt = styled.div`
  width: 50px;
  height: 50px;
  border-radius: 50%;
  background: linear-gradient(135deg, #00d4ff, #ff6b6b, #4ecdc4);
  display: flex;
  align-items: center;
  justify-content: center;
  color: white;
  font-size: 1.2rem;
  position: relative;
  animation: ${props => props.isPlaying ? rotate : 'none'} 3s linear infinite;

  &::after {
    content: '';
    position: absolute;
    width: 15px;
    height: 15px;
    background: ${props => props.theme === 'dark' ? '#000' : '#fff'};
    border-radius: 50%;
  }
`;

const TrackInfo = styled.div`
  flex: 1;
  min-width: 0;
`;

const TrackTitle = styled.div`
  font-weight: 600;
  color: ${props => props.theme === 'dark' ? '#ffffff' : '#333333'};
  font-size: 0.9rem;
  margin-bottom: 2px;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
`;

const TrackArtist = styled.div`
  font-size: 0.8rem;
  color: ${props => props.theme === 'dark' ? '#94a3b8' : '#64748b'};
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
`;

const Controls = styled.div`
  display: flex;
  align-items: center;
  gap: 10px;
`;

const ControlButton = styled(motion.button)`
  width: 35px;
  height: 35px;
  border-radius: 50%;
  border: none;
  background: ${props => props.theme === 'dark' 
    ? 'rgba(255, 255, 255, 0.1)' 
    : 'rgba(0, 0, 0, 0.1)'};
  color: ${props => props.theme === 'dark' ? '#ffffff' : '#333333'};
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  transition: all 0.3s ease;
  font-size: 1rem;

  &:hover {
    background: ${props => props.theme === 'dark' 
      ? 'rgba(255, 255, 255, 0.2)' 
