const MusicPlayer = ({ theme }) => {
  const [isPlaying, setIsPlaying] = useState(false);
  const [currentTrack, setCurrentTrack] = useState(0);
  const [progress, setProgress] = useState(0);
  const [volume, setVolume] = useState(50);
  const [isMuted, setIsMuted] = useState(false);
  const [showPlaylist, setShowPlaylist] = useState(false);
  const [shuffle, setShuffle] = useState(false);
  const [repeat, setRepeat] = useState(false);

  const playlist = [
    { title: 'Ambient Coding', artist: 'Lofi Beats', duration: '3:45' },
    { title: 'Deep Focus', artist: 'Study Music', duration: '4:20' },
    { title: 'Creative Flow', artist: 'Inspiration Mix', duration: '3:15' },
    { title: 'Night Coding', artist: 'Chill Vibes', duration: '5:10' },
    { title: 'Productivity Boost', artist: 'Energy Mix', duration: '3:55' }
  ];

  useEffect(() => {
    let interval;
    if (isPlaying) {
      interval = setInterval(() => {
        setProgress(prev => {
          if (prev >= 100) {
            setIsPlaying(false);
            return 0;
          }
          return prev + 1;
        });
      }, 1000);
    }
    return () => clearInterval(interval);
  }, [isPlaying]);

  const togglePlay = () => {
    setIsPlaying(!isPlaying);
  };

  const nextTrack = () => {
    setCurrentTrack(prev => (prev + 1) % playlist.length);
    setProgress(0);
  };

  const prevTrack = () => {
    setCurrentTrack(prev => (prev - 1 + playlist.length) % playlist.length);
    setProgress(0);
  };

  const toggleMute = () => {
    setIsMuted(!isMuted);
  };

  const toggleShuffle = () => {
    setShuffle(!shuffle);
  };

  const toggleRepeat = () => {
    setRepeat(!repeat);
  };

  const handleProgressClick = (e) => {
    const rect = e.currentTarget.getBoundingClientRect();
    const clickX = e.clientX - rect.left;
    const newProgress = (clickX / rect.width) * 100;
    setProgress(newProgress);
  };

  const currentSong = playlist[currentTrack];

  return (
    <>
      <PlayerContainer
        theme={theme}
        initial={{ y: 100, opacity: 0 }}
        animate={{ y: 0, opacity: 1 }}
        transition={{ duration: 0.5 }}
      >
        <AlbumArt 
          theme={theme} 
          isPlaying={isPlaying}
          onClick={togglePlay}
        >
          <FiMusic />
        </AlbumArt>

        <TrackInfo>
          <TrackTitle theme={theme}>{currentSong.title}</TrackTitle>
          <TrackArtist theme={theme}>{currentSong.artist}</TrackArtist>
          <ProgressBar theme={theme} onClick={handleProgressClick}>
            <ProgressFill progress={progress} />
          </ProgressBar>
        </TrackInfo>

        <Controls>
          <ControlButton
            theme={theme}
            onClick={toggleShuffle}
            className={shuffle ? 'active' : ''}
            whileHover={{ scale: 1.1 }}
            whileTap={{ scale: 0.9 }}
          >
            <FiShuffle />
          </ControlButton>

          <ControlButton
            theme={theme}
            onClick={prevTrack}
            whileHover={{ scale: 1.1 }}
            whileTap={{ scale: 0.9 }}
          >
            <FiSkipBack />
          </ControlButton>

          <ControlButton
            theme={theme}
            className="play-pause"
            onClick={togglePlay}
            whileHover={{ scale: 1.1 }}
            whileTap={{ scale: 0.9 }}
          >
            {isPlaying ? <FiPause /> : <FiPlay />}
          </ControlButton>

          <ControlButton
            theme={theme}
            onClick={nextTrack}
            whileHover={{ scale: 1.1 }}
            whileTap={{ scale: 0.9 }}
          >
            <FiSkipForward />
          </ControlButton>

          <ControlButton
            theme={theme}
            onClick={toggleRepeat}
            className={repeat ? 'active' : ''}
            whileHover={{ scale: 1.1 }}
            whileTap={{ scale: 0.9 }}
          >
            <FiRepeat />
          </ControlButton>
        </Controls>

        <Visualizer>
          {[...Array(8)].map((_, i) => (
            <Bar
              key={i}
              delay={i * 0.1}
              isPlaying={isPlaying}
            />
          ))}
        </Visualizer>

        <ControlButton
          theme={theme}
          onClick={toggleMute}
          whileHover={{ scale: 1.1 }}
          whileTap={{ scale: 0.9 }}
        >
          {isMuted ? <FiVolumeX /> : <FiVolume2 />}
        </ControlButton>

        <ControlButton
          theme={theme}
          onClick={() => setShowPlaylist(!showPlaylist)}
          whileHover={{ scale: 1.1 }}
          whileTap={{ scale: 0.9 }}
        >
          <FiList />
        </ControlButton>
      </PlayerContainer>

      <AnimatePresence>
        {showPlaylist && (
          <PlaylistPanel
            theme={theme}
            initial={{ y: 50, opacity: 0 }}
            animate={{ y: 0, opacity: 1 }}
            exit={{ y: 50, opacity: 0 }}
            transition={{ duration: 0.3 }}
          >
            <PlaylistTitle theme={theme}>Playlist</PlaylistTitle>
            {playlist.map((track, index) => (
              <PlaylistItem
                key={index}
                theme={theme}
                className={index === currentTrack ? 'active' : ''}
                onClick={() => {
                  setCurrentTrack(index);
                  setProgress(0);
                  setShowPlaylist(false);
                }}
              >
                <FiMusic />
                <div style={{ flex: 1 }}>
                  <div style={{ fontSize: '0.9rem', fontWeight: 500 }}>
                    {track.title}
                  </div>
                  <div style={{ fontSize: '0.8rem', opacity: 0.7 }}>
                    {track.artist} â€¢ {track.duration}
                  </div>
                </div>
                {index === currentTrack && isPlaying && (
                  <motion.div
                    animate={{ scale: [1, 1.2, 1] }}
                    transition={{ duration: 1, repeat: Infinity }}
                  >
                    <FiPlay />
                  </motion.div>
                )}
              </PlaylistItem>
            ))}
          </PlaylistPanel>
        )}
      </AnimatePresence>
    </>
  );
};

