import React, { useState, useEffect } from 'react';
import styled, { keyframes } from 'styled-components';
import { motion, AnimatePresence } from 'framer-motion';
import { 
  FiMic, FiMicOff, FiKeyboard, FiMousePointer, FiZap, 
  FiEye, FiEyeOff, FiVolume2, FiVolumeX, FiCommand,
  FiHelpCircle, FiSettings, FiPlay, FiPause, FiRotateCw
} from 'react-icons/fi';

const fadeIn = keyframes`
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
`;

const pulse = keyframes`
  0%, 100% {
    transform: scale(1);
    opacity: 1;
  }
  50% {
    transform: scale(1.05);
    opacity: 0.8;
  }
`;

const float = keyframes`
  0%, 100% {
    transform: translateY(0px);
  }
  50% {
    transform: translateY(-10px);
  }
`;

const SectionContainer = styled.section`
  min-height: 100vh;
  background: ${props => props.theme === 'dark' 
    ? 'linear-gradient(135deg, #0a0a0a 0%, #1a1a2e 50%, #16213e 100%)'
    : 'linear-gradient(135deg, #f8f9fa 0%, #e9ecef 50%, #dee2e6 100%)'};
  padding: 80px 20px;
  position: relative;
  overflow: hidden;
`;

const SectionHeader = styled.div`
  text-align: center;
  margin-bottom: 60px;
  animation: ${fadeIn} 0.8s ease-out;
`;

const SectionTitle = styled.h2`
  font-size: 3rem;
  font-weight: 700;
  background: linear-gradient(135deg, #00d4ff, #ff6b6b, #4ecdc4);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  margin-bottom: 20px;

  @media (max-width: 768px) {
    font-size: 2rem;
  }
`;

const SectionSubtitle = styled.p`
  font-size: 1.2rem;
  color: ${props => props.theme === 'dark' ? '#94a3b8' : '#64748b'};
  max-width: 600px;
  margin: 0 auto;
  line-height: 1.6;
`;

const FeaturesGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  gap: 30px;
  max-width: 1200px;
  margin: 0 auto;
`;

const FeatureCard = styled(motion.div)`
  background: ${props => props.theme === 'dark' 
    ? 'rgba(255, 255, 255, 0.05)' 
    : 'rgba(255, 255, 255, 0.8)'};
  border: 1px solid ${props => props.theme === 'dark' 
    ? 'rgba(255, 255, 255, 0.1)' 
    : 'rgba(0, 0, 0, 0.1)'};
  border-radius: 20px;
  padding: 30px;
  backdrop-filter: blur(10px);
  cursor: pointer;
  transition: all 0.3s ease;
  position: relative;
  overflow: hidden;

  &:hover {
    transform: translateY(-5px);
    box-shadow: 0 20px 40px rgba(0, 0, 0, 0.2);
    border-color: ${props => props.theme === 'dark' ? '#00d4ff' : '#007bff'};
  }

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.1), transparent);
    transition: left 0.5s ease;
  }

  &:hover::before {
    left: 100%;
  }
`;

const FeatureIcon = styled.div`
  width: 60px;
  height: 60px;
  border-radius: 50%;
  background: linear-gradient(135deg, ${props => props.color}, ${props => props.color}dd);
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 1.5rem;
  color: white;
  margin-bottom: 20px;
  animation: ${pulse} 2s ease-in-out infinite;
`;

const FeatureTitle = styled.h3`
  font-size: 1.5rem;
  font-weight: 600;
  color: ${props => props.theme === 'dark' ? '#ffffff' : '#333333'};
  margin-bottom: 15px;
`;

const FeatureDescription = styled.p`
  color: ${props => props.theme === 'dark' ? '#94a3b8' : '#64748b'};
  line-height: 1.6;
  margin-bottom: 20px;
`;

const FeatureStatus = styled.div`
  display: flex;
  align-items: center;
  gap: 10px;
  font-size: 0.9rem;
  color: ${props => props.active 
    ? (props.theme === 'dark' ? '#00d4ff' : '#007bff')
    : (props.theme === 'dark' ? '#64748b' : '#94a3b8')};
`;

const StatusDot = styled.div`
  width: 8px;
  height: 8px;
  border-radius: 50%;
  background: ${props => props.active 
    ? (props.theme === 'dark' ? '#00d4ff' : '#007bff')
    : (props.theme === 'dark' ? '#64748b' : '#94a3b8')};
  animation: ${props => props.active ? pulse : 'none'} 2s ease-in-out infinite;
`;

const ControlsPanel = styled.div`
  position: fixed;
  top: 20px;
  right: 20px;
  display: flex;
  flex-direction: column;
  gap: 10px;
  z-index: 1000;
`;

const ControlButton = styled(motion.button)`
  width: 50px;
  height: 50px;
  border-radius: 50%;
  border: none;
  background: ${props => props.theme === 'dark' 
    ? 'rgba(255, 255, 255, 0.1)' 
    : 'rgba(0, 0, 0, 0.1)'};
  color: ${props => props.theme === 'dark' ? '#ffffff' : '#333333'};
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  backdrop-filter: blur(10px);
  transition: all 0.3s ease;
  font-size: 1.2rem;

  &:hover {
    background: ${props => props.theme === 'dark' 
      ? 'rgba(255, 255, 255, 0.2)' 
      : 'rgba(0, 0, 0, 0.2)'};
    transform: scale(1.1);
  }

  &.active {
    background: ${props => props.theme === 'dark' ? '#00d4ff' : '#007bff'};
    color: white;
  }
`;

const Notification = styled(motion.div)`
  position: fixed;
  top: 100px;
  right: 20px;
  background: ${props => props.theme === 'dark' 
    ? 'rgba(0, 0, 0, 0.9)' 
    : 'rgba(255, 255, 255, 0.9)'};
  color: ${props => props.theme === 'dark' ? '#ffffff' : '#333333'};
  padding: 15px 20px;
  border-radius: 10px;
  border: 1px solid ${props => props.theme === 'dark' 
    ? 'rgba(255, 255, 255, 0.1)' 
    : 'rgba(0, 0, 0, 0.1)'};
  backdrop-filter: blur(10px);
  z-index: 1001;
  max-width: 300px;
  box-shadow: 0 10px 30px rgba(0, 0, 0, 0.3);
`;

const InteractiveDemo = styled.div`
  width: 100%;
  height: 200px;
  background: ${props => props.theme === 'dark' 
    ? 'rgba(255, 255, 255, 0.05)' 
    : 'rgba(255, 255, 255, 0.5)'};
  border-radius: 15px;
  display: flex;
  align-items: center;
  justify-content: center;
  margin-top: 20px;
  position: relative;
  overflow: hidden;
  border: 2px dashed ${props => props.theme === 'dark' 
    ? 'rgba(255, 255, 255, 0.2)' 
    : 'rgba(0, 0, 0, 0.2)'};
`;

const DemoElement = styled(motion.div)`
  width: 60px;
  height: 60px;
  background: linear-gradient(135deg, #00d4ff, #ff6b6b);
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  color: white;
  font-size: 1.5rem;
  cursor: pointer;
  animation: ${float} 3s ease-in-out infinite;
`;

const InteractiveFeatures = ({ theme }) => {
  const [features, setFeatures] = useState({
    voiceCommands: false,
    gestureControls: false,
    keyboardShortcuts: false,
    eyeTracking: false,
    soundEffects: false,
    autoPlay: false
  });

  const [notifications, setNotifications] = useState([]);
  const [isListening, setIsListening] = useState(false);
  const [currentGesture, setCurrentGesture] = useState('');

  // Voice Commands
  useEffect(() => {
    if (features.voiceCommands && isListening) {
      // Simulate voice recognition
      const commands = [
        'scroll down',
        'scroll up',
        'go to projects',
        'go to contact',
        'toggle theme',
        'play music'
      ];

      const interval = setInterval(() => {
        const randomCommand = commands[Math.floor(Math.random() * commands.length)];
        showNotification(`Voice command: "${randomCommand}"`);
      }, 5000);

      return () => clearInterval(interval);
    }
  }, [features.voiceCommands, isListening]);

  // Keyboard Shortcuts
  useEffect(() => {
    const handleKeyPress = (e) => {
      if (!features.keyboardShortcuts) return;

      if (e.ctrlKey || e.metaKey) {
        switch(e.key) {
          case '1':
            showNotification('Keyboard shortcut: Go to Home (Ctrl+1)');
            break;
          case '2':
            showNotification('Keyboard shortcut: Go to Projects (Ctrl+2)');
            break;
          case '3':
            showNotification('Keyboard shortcut: Go to Contact (Ctrl+3)');
            break;
          case 't':
            showNotification('Keyboard shortcut: Toggle Theme (Ctrl+T)');
            break;
          case 'm':
            showNotification('Keyboard shortcut: Toggle Music (Ctrl+M)');
            break;
        }
      }
    };

    window.addEventListener('keydown', handleKeyPress);
    return () => window.removeEventListener('keydown', handleKeyPress);
  }, [features.keyboardShortcuts]);

  // Gesture Controls
  useEffect(() => {
    let startY = 0;
    let startX = 0;

    const handleTouchStart = (e) => {
      if (!features.gestureControls) return;
      startY = e.touches[0].clientY;
      startX = e.touches[0].clientX;
    };

    const handleTouchEnd = (e) => {
      if (!features.gestureControls) return;
      const endY = e.changedTouches[0].clientY;
      const endX = e.changedTouches[0].clientX;
      const deltaY = endY - startY;
      const deltaX = endX - startX;

      if (Math.abs(deltaY) > Math.abs(deltaX)) {
        if (deltaY > 50) {
          setCurrentGesture('Swipe Down');
          showNotification('Gesture: Swipe Down - Scroll Up');
        } else if (deltaY < -50) {
          setCurrentGesture('Swipe Up');
          showNotification('Gesture: Swipe Up - Scroll Down');
        }
      } else {
        if (deltaX > 50) {
          setCurrentGesture('Swipe Right');
          showNotification('Gesture: Swipe Right - Next Section');
        } else if (deltaX < -50) {
          setCurrentGesture('Swipe Left');
          showNotification('Gesture: Swipe Left - Previous Section');
        }
      }
    };

    window.addEventListener('touchstart', handleTouchStart);
    window.addEventListener('touchend', handleTouchEnd);

    return () => {
      window.removeEventListener('touchstart', handleTouchStart);
      window.removeEventListener('touchend', handleTouchEnd);
    };
  }, [features.gestureControls]);

  const showNotification = (message) => {
    const id = Date.now();
    setNotifications(prev => [...prev, { id, message }]);
    setTimeout(() => {
      setNotifications(prev => prev.filter(n => n.id !== id));
    }, 3000);
  };

  const toggleFeature = (featureName) => {
    setFeatures(prev => ({
      ...prev,
      [featureName]: !prev[featureName]
    }));

    const action = features[featureName] ? 'disabled' : 'enabled';
    showNotification(`${featureName.replace(/([A-Z])/g, ' $1')} ${action}`);
  };

  const toggleVoiceListening = () => {
    setIsListening(!isListening);
    showNotification(isListening ? 'Voice recognition stopped' : 'Voice recognition started');
  };

  const featuresList = [
    {
      name: 'voiceCommands',
      title: 'Voice Commands',
      description: 'Control the portfolio using voice commands. Say "scroll down", "go to projects", or "toggle theme".',
      icon: <FiMic />,
      color: '#00d4ff',
      demo: (
        <InteractiveDemo theme={theme}>
          <DemoElement
            animate={{ scale: isListening ? 1.2 : 1 }}
            transition={{ duration: 0.3 }}
            onClick={toggleVoiceListening}
          >
            {isListening ? <FiMicOff /> : <FiMic />}
          </DemoElement>
        </InteractiveDemo>
      )
    },
    {
      name: 'gestureControls',
      title: 'Gesture Controls',
      description: 'Navigate using touch gestures. Swipe up/down to scroll, left/right to change sections.',
      icon: <FiMousePointer />,
      color: '#ff6b6b',
      demo: (
        <InteractiveDemo theme={theme}>
          <DemoElement
            animate={{ 
              rotate: currentGesture.includes('Swipe') ? 360 : 0,
              scale: currentGesture ? 1.3 : 1
            }}
            transition={{ duration: 0.5 }}
          >
            <FiMousePointer />
          </DemoElement>
          {currentGesture && (
            <motion.div
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              style={{
                position: 'absolute',
                bottom: '20px',
                color: theme === 'dark' ? '#ffffff' : '#333333',
                fontSize: '0.9rem'
              }}
            >
              {currentGesture}
            </motion.div>
          )}
        </InteractiveDemo>
      )
    },
    {
      name: 'keyboardShortcuts',
      title: 'Keyboard Shortcuts',
      description: 'Use keyboard shortcuts for quick navigation. Ctrl+1 (Home), Ctrl+2 (Projects), Ctrl+T (Theme).',
      icon: <FiKeyboard />,
      color: '#4ecdc4',
      demo: (
        <InteractiveDemo theme={theme}>
          <DemoElement
            animate={{ 
              y: [0, -10, 0],
              rotate: [0, 5, -5, 0]
            }}
            transition={{ 
              duration: 2,
              repeat: Infinity,
              ease: "easeInOut"
            }}
          >
            <FiKeyboard />
          </DemoElement>
        </InteractiveDemo>
      )
    },
    {
      name: 'eyeTracking',
      title: 'Eye Tracking',
      description: 'Advanced eye tracking for hands-free navigation. Look at elements to highlight them.',
      icon: <FiEye />,
      color: '#f59e0b',
      demo: (
        <InteractiveDemo theme={theme}>
          <DemoElement
            animate={{ 
              scale: [1, 1.1, 1],
              opacity: [0.7, 1, 0.7]
            }}
            transition={{ 
              duration: 1.5,
              repeat: Infinity,
              ease: "easeInOut"
            }}
          >
            <FiEye />
          </DemoElement>
        </InteractiveDemo>
      )
    },
    {
      name: 'soundEffects',
      title: 'Sound Effects',
      description: 'Immersive audio feedback for interactions. Toggle sounds for clicks, hovers, and transitions.',
      icon: <FiVolume2 />,
      color: '#8b5cf6',
      demo: (
        <InteractiveDemo theme={theme}>
          <DemoElement
            animate={{ 
              scale: [1, 1.2, 1],
              rotate: [0, 10, -10, 0]
            }}
            transition={{ 
              duration: 1,
              repeat: Infinity,
              ease: "easeInOut"
            }}
          >
            <FiVolume2 />
          </DemoElement>
        </InteractiveDemo>
      )
    },
    {
      name: 'autoPlay',
      title: 'Auto Play',
      description: 'Automatic content progression and animations. Sit back and watch the portfolio come alive.',
      icon: <FiPlay />,
      color: '#ec4899',
      demo: (
        <InteractiveDemo theme={theme}>
          <DemoElement
            animate={{ 
              x: [0, 100, 0],
              rotate: [0, 180, 360]
            }}
            transition={{ 
              duration: 3,
              repeat: Infinity,
              ease: "easeInOut"
            }}
          >
            <FiPlay />
          </DemoElement>
        </InteractiveDemo>
      )
    }
  ];

  return (
    <SectionContainer theme={theme}>
      <SectionHeader>
        <SectionTitle>Interactive Features</SectionTitle>
        <SectionSubtitle theme={theme}>
          Experience the future of web interaction with advanced features powered by modern web technologies
        </SectionSubtitle>
      </SectionHeader>

      <FeaturesGrid>
        {featuresList.map((feature) => (
          <FeatureCard
            key={feature.name}
            theme={theme}
            whileHover={{ scale: 1.02 }}
            whileTap={{ scale: 0.98 }}
            onClick={() => toggleFeature(feature.name)}
          >
            <FeatureIcon color={feature.color}>
              {feature.icon}
            </FeatureIcon>
            <FeatureTitle theme={theme}>{feature.title}</FeatureTitle>
            <FeatureDescription theme={theme}>
              {feature.description}
            </FeatureDescription>
            <FeatureStatus active={features[feature.name]} theme={theme}>
              <StatusDot active={features[feature.name]} />
              {features[feature.name] ? 'Active' : 'Inactive'}
            </FeatureStatus>
            {feature.demo}
          </FeatureCard>
        ))}
      </FeaturesGrid>

      <ControlsPanel>
        <ControlButton
          theme={theme}
          className={isListening ? 'active' : ''}
          onClick={toggleVoiceListening}
          whileHover={{ scale: 1.1 }}
          whileTap={{ scale: 0.9 }}
        >
          {isListening ? <FiMicOff /> : <FiMic />}
        </ControlButton>
        <ControlButton
          theme={theme}
          onClick={() => showNotification('Settings panel opened')}
          whileHover={{ scale: 1.1 }}
          whileTap={{ scale: 0.9 }}
        >
          <FiSettings />
        </ControlButton>
        <ControlButton
          theme={theme}
          onClick={() => showNotification('Help guide opened')}
          whileHover={{ scale: 1.1 }}
          whileTap={{ scale: 0.9 }}
        >
          <FiHelpCircle />
        </ControlButton>
      </ControlsPanel>

      <AnimatePresence>
        {notifications.map((notification) => (
          <Notification
            key={notification.id}
            theme={theme}
            initial={{ opacity: 0, x: 300 }}
            animate={{ opacity: 1, x: 0 }}
            exit={{ opacity: 0, x: 300 }}
            transition={{ duration: 0.3 }}
          >
            {notification.message}
          </Notification>
        ))}
      </AnimatePresence>
    </SectionContainer>
  );
};

export default InteractiveFeatures;
