import React, { useRef, useState } from 'react';
import { Canvas, useFrame } from '@react-three/fiber';
import { Box, Sphere, Cylinder, Torus, OrbitControls, Text } from '@react-three/drei';
import { motion } from 'framer-motion-3d';
import styled from 'styled-components';

const ShowcaseContainer = styled.div`
  width: 100%;
  height: 500px;
  position: relative;
  background: ${props => props.theme === 'dark' 
    ? 'linear-gradient(135deg, #1a1a2e 0%, #16213e 50%, #0f3460 100%)'
    : 'linear-gradient(135deg, #f8f9fa 0%, #e9ecef 50%, #dee2e6 100%)'};
`;

const ProjectModel = ({ type, position, color, hovered, onHover, theme }) => {
  const meshRef = useRef();

  useFrame((state) => {
    if (meshRef.current) {
      meshRef.current.rotation.y = state.clock.elapsedTime * 0.5;
      meshRef.current.rotation.x = state.clock.elapsedTime * 0.3;
    }
  });

  const getGeometry = () => {
    switch (type) {
      case 'ai':
        return <sphereGeometry args={[0.5, 32, 32]} />;
      case 'web':
        return <boxGeometry args={[1, 1, 1]} />;
      case 'mobile':
        return <cylinderGeometry args={[0.5, 0.5, 1, 32]} />;
      case 'data':
        return <torusGeometry args={[0.5, 0.2, 16, 32]} />;
      default:
        return <boxGeometry args={[0.8, 0.8, 0.8]} />;
    }
  };

  return (
    <motion.group
      ref={meshRef}
      position={position}
      animate={{
        scale: hovered ? 1.2 : 1,
        y: hovered ? position[1] + 0.2 : position[1],
      }}
      transition={{ duration: 0.3 }}
      onPointerEnter={() => onHover(true)}
      onPointerLeave={() => onHover(false)}
    >
      <mesh>
        {getGeometry()}
        <meshStandardMaterial
          color={color}
          metalness={0.8}
          roughness={0.2}
          emissive={hovered ? color : '#000000'}
          emissiveIntensity={hovered ? 0.3 : 0}
        />
      </mesh>

      {hovered && (
        <Text
          position={[0, 1.2, 0]}
          fontSize={0.2}
          color={theme === 'dark' ? '#ffffff' : '#333333'}
          anchorX="center"
          anchorY="middle"
        >
          {type.toUpperCase()}
        </Text>
      )}
    </motion.group>
  );
};

const FloatingParticles = () => {
  const particlesRef = useRef();
  const particleCount = 50;

  useFrame((state) => {
    if (particlesRef.current) {
      const positions = particlesRef.current.geometry.attributes.position.array;
      for (let i = 0; i < particleCount * 3; i += 3) {
        positions[i + 1] += Math.sin(state.clock.elapsedTime + i) * 0.005;
        positions[i] += Math.cos(state.clock.elapsedTime + i) * 0.005;
      }
      particlesRef.current.geometry.attributes.position.needsUpdate = true;
    }
  });

  return (
    <points ref={particlesRef}>
      <bufferGeometry>
        <bufferAttribute
          attach="attributes-position"
          count={particleCount}
          array={new Float32Array(particleCount * 3).map(() => (Math.random() - 0.5) * 8)}
          itemSize={3}
        />
      </bufferGeometry>
      <pointsMaterial
        size={0.03}
        color="#4ecdc4"
        transparent
        opacity={0.6}
        sizeAttenuation
      />
    </points>
  );
};

const ProjectShowcase = ({ theme }) => {
  const [hoveredProject, setHoveredProject] = useState(null);

  const projects = [
    {
      type: 'ai',
      position: [-2, 0, 0],
      color: '#ff6b6b',
      name: 'AI/ML Projects'
    },
    {
      type: 'web',
      position: [2, 0, 0],
      color: '#4ecdc4',
      name: 'Web Applications'
    },
    {
      type: 'mobile',
      position: [0, 0, -2],
      color: '#45b7d1',
      name: 'Mobile Apps'
    },
    {
      type: 'data',
      position: [0, 0, 2],
      color: '#96ceb4',
      name: 'Data Engineering'
    }
  ];

  return (
    <ShowcaseContainer theme={theme}>
      <Canvas
        camera={{ position: [0, 0, 5], fov: 75 }}
        style={{ background: 'transparent' }}
      >
        <ambientLight intensity={0.4} />
        <pointLight position={[10, 10, 10]} intensity={1} />
        <pointLight position={[-10, -10, -10]} intensity={0.5} />
        <pointLight position={[0, 10, 0]} intensity={0.8} />

        {projects.map((project, index) => (
          <ProjectModel
            key={index}
            type={project.type}
            position={project.position}
            color={project.color}
            hovered={hoveredProject === index}
            onHover={(hovered) => setHoveredProject(hovered ? index : null)}
            theme={theme}
          />
        ))}

        <FloatingParticles />

        <OrbitControls
          enableZoom={true}
          enablePan={false}
          minDistance={3}
          maxDistance={8}
          autoRotate
          autoRotateSpeed={0.3}
        />
      </Canvas>
    </ShowcaseContainer>
  );
};

export default ProjectShowcase;
