import React, { useState, useEffect } from 'react';
import styled, { keyframes } from 'styled-components';
import { motion, AnimatePresence } from 'framer-motion';
import { 
  FiMapPin, FiThermometer, FiDroplet, FiWind, 
  FiSun, FiCloud, FiCloudRain, FiCloudSnow,
  FiRefreshCw, FiNavigation, FiEye, FiClock
} from 'react-icons/fi';

const fadeIn = keyframes`
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
`;

const float = keyframes`
  0%, 100% {
    transform: translateY(0px);
  }
  50% {
    transform: translateY(-10px);
  }
`;

const rotate = keyframes`
  from {
    transform: rotate(0deg);
  }
  to {
    transform: rotate(360deg);
  }
`;

const pulse = keyframes`
  0%, 100% {
    transform: scale(1);
  }
  50% {
    transform: scale(1.05);
  }
`;

const rain = keyframes`
  0% {
    transform: translateY(-10px) translateX(0);
    opacity: 0;
  }
  50% {
    opacity: 1;
  }
  100% {
    transform: translateY(20px) translateX(10px);
    opacity: 0;
  }
`;

const snow = keyframes`
  0% {
    transform: translateY(-10px) translateX(0) rotate(0deg);
    opacity: 0;
  }
  50% {
    opacity: 1;
  }
  100% {
    transform: translateY(20px) translateX(10px) rotate(360deg);
    opacity: 0;
  }
`;

const WidgetContainer = styled(motion.div)`
  position: fixed;
  top: 20px;
  left: 20px;
  background: ${props => props.theme === 'dark' 
    ? 'rgba(0, 0, 0, 0.9)' 
    : 'rgba(255, 255, 255, 0.9)'};
  border: 1px solid ${props => props.theme === 'dark' 
    ? 'rgba(255, 255, 255, 0.1)' 
    : 'rgba(0, 0, 0, 0.1)'};
  border-radius: 20px;
  padding: 20px;
  backdrop-filter: blur(20px);
  box-shadow: 0 10px 30px rgba(0, 0, 0, 0.3);
  z-index: 1000;
  min-width: 280px;
  max-width: 350px;

  @media (max-width: 768px) {
    position: relative;
    top: auto;
    left: auto;
    margin: 20px auto;
    max-width: 100%;
  }
`;

const LocationHeader = styled.div`
  display: flex;
  align-items: center;
  gap: 10px;
  margin-bottom: 15px;
`;

const LocationIcon = styled.div`
  color: ${props => props.theme === 'dark' ? '#00d4ff' : '#007bff'};
  font-size: 1.2rem;
`;

const LocationText = styled.div`
  color: ${props => props.theme === 'dark' ? '#ffffff' : '#333333'};
  font-weight: 600;
  font-size: 1.1rem;
`;

const WeatherMain = styled.div`
  display: flex;
  align-items: center;
  gap: 20px;
  margin-bottom: 20px;
`;

const WeatherIcon = styled.div`
  width: 80px;
  height: 80px;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 3rem;
  color: ${props => props.color || '#ffd700'};
  animation: ${float} 3s ease-in-out infinite;
  position: relative;
`;

const RainDrop = styled.div`
  position: absolute;
  width: 2px;
  height: 10px;
  background: #00d4ff;
  border-radius: 50%;
  animation: ${rain} 1s linear infinite;
  animation-delay: ${props => props.delay}s;
  left: ${props => props.left}px;
  top: 0;
