const WeatherWidget = ({ theme }) => {
  const [weather, setWeather] = useState(null);
  const [loading, setLoading] = useState(true);
  const [location, setLocation] = useState('San Francisco, CA');
  const [forecast, setForecast] = useState([]);

  // Simulated weather data
  const mockWeatherData = {
    current: {
      temp: 22,
      feels_like: 24,
      humidity: 65,
      wind_speed: 12,
      visibility: 10,
      description: 'Partly Cloudy',
      icon: 'cloud'
    },
    forecast: [
      { time: 'Now', temp: 22, icon: 'cloud' },
      { time: '1PM', temp: 24, icon: 'sun' },
      { time: '2PM', temp: 25, icon: 'sun' },
      { time: '3PM', temp: 23, icon: 'cloud' },
      { time: '4PM', temp: 21, icon: 'cloud-rain' },
      { time: '5PM', temp: 19, icon: 'cloud-rain' }
    ]
  };

  useEffect(() => {
    // Simulate API call
    const fetchWeather = async () => {
      setLoading(true);
      await new Promise(resolve => setTimeout(resolve, 1500));
      setWeather(mockWeatherData.current);
      setForecast(mockWeatherData.forecast);
      setLoading(false);
    };

    fetchWeather();
  }, [location]);

  const getWeatherIcon = (iconType) => {
    switch (iconType) {
      case 'sun':
        return <FiSun style={{ color: '#ffd700' }} />;
      case 'cloud':
        return <FiCloud style={{ color: '#87ceeb' }} />;
      case 'cloud-rain':
        return <FiCloudRain style={{ color: '#4682b4' }} />;
      case 'cloud-snow':
        return <FiCloudSnow style={{ color: '#ffffff' }} />;
      default:
        return <FiSun style={{ color: '#ffd700' }} />;
    }
  };

  const getWeatherColor = (iconType) => {
    switch (iconType) {
      case 'sun':
        return '#ffd700';
      case 'cloud':
        return '#87ceeb';
      case 'cloud-rain':
        return '#4682b4';
      case 'cloud-snow':
        return '#ffffff';
      default:
        return '#ffd700';
    }
  };

  const handleRefresh = async () => {
    setLoading(true);
    await new Promise(resolve => setTimeout(resolve, 1000));
    setLoading(false);
  };

  if (loading) {
    return (
      <WidgetContainer theme={theme}>
        <LoadingSpinner theme={theme}>
          <FiRefreshCw />
        </LoadingSpinner>
      </WidgetContainer>
    );
  }

  return (
    <WidgetContainer
      theme={theme}
      initial={{ opacity: 0, y: -50 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.5 }}
    >
      <RefreshButton
        theme={theme}
        onClick={handleRefresh}
        className={loading ? 'loading' : ''}
        whileHover={{ scale: 1.1 }}
        whileTap={{ scale: 0.9 }}
      >
        <FiRefreshCw />
      </RefreshButton>

      <LocationHeader>
        <LocationIcon theme={theme}>
          <FiMapPin />
        </LocationIcon>
        <LocationText theme={theme}>{location}</LocationText>
      </LocationHeader>

      <WeatherMain>
        <WeatherIcon color={getWeatherColor(weather.icon)}>
          {getWeatherIcon(weather.icon)}
          {weather.icon === 'cloud-rain' && (
            <>
              {[...Array(5)].map((_, i) => (
                <RainDrop key={i} delay={i * 0.2} left={i * 8} />
              ))}
            </>
          )}
          {weather.icon === 'cloud-snow' && (
            <>
              {[...Array(5)].map((_, i) => (
                <SnowFlake key={i} delay={i * 0.3} left={i * 8} />
              ))}
            </>
          )}
        </WeatherIcon>
        <div>
          <Temperature theme={theme}>{weather.temp}°C</Temperature>
          <WeatherDescription theme={theme}>
            {weather.description}
          </WeatherDescription>
        </div>
      </WeatherMain>

      <WeatherDetails>
        <DetailItem theme={theme}>
          <DetailIcon theme={theme}>
            <FiThermometer />
          </DetailIcon>
          Feels like {weather.feels_like}°C
        </DetailItem>
        <DetailItem theme={theme}>
          <DetailIcon theme={theme}>
            <FiDroplet />
          </DetailIcon>
          {weather.humidity}% humidity
        </DetailItem>
        <DetailItem theme={theme}>
          <DetailIcon theme={theme}>
            <FiWind />
          </DetailIcon>
          {weather.wind_speed} km/h
        </DetailItem>
        <DetailItem theme={theme}>
          <DetailIcon theme={theme}>
            <FiEye />
          </DetailIcon>
          {weather.visibility} km
        </DetailItem>
      </WeatherDetails>

      <ForecastContainer>
        {forecast.map((item, index) => (
          <ForecastItem key={index} theme={theme}>
            <ForecastTime theme={theme}>{item.time}</ForecastTime>
            <div style={{ fontSize: '1.5rem', color: getWeatherColor(item.icon) }}>
              {getWeatherIcon(item.icon)}
            </div>
            <ForecastTemp theme={theme}>{item.temp}°</ForecastTemp>
          </ForecastItem>
        ))}
      </ForecastContainer>
    </WidgetContainer>
  );
};

export default WeatherWidget;
